style.css:
@-moz-document domain("jungefreiheit.de") {
/* ==UserStyle==
@name         JUNGEFREIHEIT.DE Dark Theme (2020)
@namespace    de.sidneys.userstyles
@version      2.1.0
@description  Dark theme for JUNGE FREIHEIT – WOCHENZEITUNG FÜR DEBATTE / Dunkles Farbschema für JUNGE FREIHEIT – WOCHENZEITUNG FÜR DEBATTE 
@author       sidneys <sidneys.github.io@outlook.com> (https://github.com/sidneys)
@homepageURL  https://userstyles.org/users/301302
@supportURL   https://forum.userstyles.org/messages/add/sidneys
@license      MIT
@preprocessor default
==/UserStyle== */
}

@-moz-document domain("jungefreiheit.de") {
/* ====================================
   VARIABLES
   ==================================== */

:root
{
  --color-container-1: rgb(10, 10, 10);
  --color-container-2: rgb(40, 40, 40);
  --color-container-2-translucent: rgba(40, 40, 40, 0.85);
  --color-container-3: rgb(50, 50, 50);
  --color-container-4: rgb(60, 60, 60);
  --color-container-5: rgb(30, 30, 30);
  --color-text-1: rgb(160, 160, 160);
  --color-text-2: rgb(190, 190, 190);
  --color-text-3: rgb(200, 200, 200);
  --color-text-4: rgb(230, 230, 230);
  --color-text-5: rgb(255, 255, 255);
  --color-text-5-translucent: rgba(255, 255, 255, 0.25);
  --color-border-1: rgb(10, 10, 10);
  --color-border-2: rgb(40, 40, 40);
  --color-border-3: rgb(50, 50, 50);
  --color-border-4: rgb(100, 100, 100);
  --color-border-5: rgb(140, 140, 140);
}
}

@-moz-document domain("jungefreiheit.de") {
/* ====================================
   ELEMENTS
   ==================================== */

html, 
html *
{
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: grayscale;
}


/* ====================================
   CONTAINER
   ==================================== */

.elementor-element-4c0a3865:not(.elementor-motion-effects-element-type-background),
.elementor-element > .elementor-motion-effects-container > .elementor-motion-effects-layer,
.elementor-element > .elementor-background-overlay

{
    background-color: var(--color-container-1) !important;
}

.elementor-nav-menu--dropdown {
	background-color: var(--color-container-2-translucent) !important;
}

.elementor-222229 .elementor-element.elementor-element-1a11610a .elementor-nav-menu--main .elementor-item
{
    color: var(--color-text-5) !important;
}

#elementor-popup-modal-222279 .dialog-widget-content,
#elementor-popup-modal-222279 .dialog-message,
.elementor-element-66980cd1 .menu-item-222602 
{
    background-color: var(--color-container-3);
}

body,
.elementor-element:not(.elementor-motion-effects-element-type-background), 
.elementor-element > .elementor-background-overlay,
.elementor-element:not(.elementor-motion-effects-element-type-background) > .elementor-element-populated,
.elementor-element .elementor-divider-separator
{
    background-color: var(--color-container-3) !important;
    opacity: 1 !important;
}

.elementor-element:not(.elementor-motion-effects-element-type-background) > .elementor-element-populated
{
    opacity: 0.90 !important;
}

.elementor-222229 .elementor-element.elementor-element-54c909b8 .elementor-divider-separator {
    border-color: var(--color-container-3);
}

.elementor-element-39e06b72 .elementor-element-310dbb4f
{
    background-color: transparent !important;
}


.elementor-222229 .elementor-element.elementor-element-39e06b72,
.elementor-222229 .elementor-element.elementor-element-66980cd1 > .elementor-widget-container,
.elementor-222229 .elementor-element.elementor-element-76dad34e > .elementor-widget-container,
.elementor-222261 .elementor-element.elementor-element-2bcfe878 > .elementor-element-populated,
.elementor-222261 .elementor-element.elementor-element-6c21e3b8 > .elementor-element-populated,
.elementor-222261 .elementor-element.elementor-element-1dfb3dd5 .elementor-divider-separator,
.elementor-222261 .elementor-element.elementor-element-70fb58db .elementor-divider-separator
{
    border-color: var(--color-text-5-translucent);
}

.elementor-element-22086fb0 .elementor-image img
{
    filter: invert(100%) !important;
}
.elementor-213 .elementor-element.elementor-element-9f07891,
.elementor-213 .elementor-element.elementor-element-6b468634
{
    color: var(--color-text-2) !important;
}  


.elementor-widget-posts-extra .ee-post__excerpt a,
.elementor-widget-theme-post-content,
.ee-post__excerpt,
.elementor-widget-text-editor
{
    color: var(--color-text-3) !important;
}

.elementor-heading-title,
.elementor-element .ee-post__title__heading,
.post-navigation__prev--title,
.post-navigation__next--title,
.post-navigation__arrow-wrapper,
.ee-post__terms__link,
.excerpt-link
{
    color: var(--color-text-5) !important;
}

.elementor-nav-menu--main .elementor-item,
.elementor-post__meta-data,
.ee-post__meta--comments a,
.elementor-post-avatar a
{
    color: var(--color-text-4) !important;
}

.ee-post,
.ee-post__body
{
    background-color: var(--color-container-3) !important;
}

.ee-post,
.ee-post__header,
.ee-post__footer,
.elementor-post-navigation-borders-yes .elementor-post-navigation.elementor-grid
{
    border-color: var(--color-border-5) !important;
    border-width: 0.5px !important;
}

.elementor-post-navigation-borders-yes .elementor-post-navigation__separator{
	width: 0.5px !important;
	background-color: var(--color-border-5) !important;
}

.ee-post,
.ee-post__header
{
    border-right-width: 0 !important;
    border-bottom-width: 0 !important;
}


ol.wpp-list li span.wpp-stats,
.ee-post__meta,
.elementor-posts-nothing-found
{

    color: var(--color-text-1) !important;

}

.elementor-element-3f663b8d .elementor-element-populated,
.elementor-element-580c4baf .elementor-element-populated {
    border-color: var(--color-border-5) !important;
}

.elementor-element-populated {
    border-color: var(--color-border-2) !important;
}

.ee-post__footer .ee-post__meta--comments img {
    filter: invert(100%)
}

.elementor-222229 .elementor-element.elementor-element-310dbb4f .elementor-image img {
    outline: 20px solid var(--color-container-3);
    outline-offset: -20px;
}


.elementor-222229 .elementor-element.elementor-element-310dbb4f > .elementor-widget-container {
    margin-top: -40px;
}

.elementor-222229 .elementor-element.elementor-element-20cb08e7 {
    display: none;
}

a
{
   color: rgb(237, 51, 102) !important;
}

a:active,
a:hover
{
    color: hsl(195, 97%, 54%) !important;
}

.elementor-search-form__container
{
    background-color: var(--color-container-2) !important;
}

input, select, textarea {
	box-shadow: 1px 1px 1px hsl(0, 0%, 30%) inset;
}

.elementor-search-form__input,
.elementor-search-form__icon,
.dialog-lightbox-close-button:hover,
.elementor-search-form__input {
	color: #fff !important;
}

ol.comment-list li.comment,
.comment-info-box
{
	background-color: var(--color-container-5) !important;
}

.highlight-box
{
	background-color: var(--color-container-4) !important;
    color: #fff !important;
}

.comment-meta,
.comment-list p,
.comment-info-box
{
    color: var(--color-text-4) !important;
}

li.comment,
.comment-info-box
{
	border-color: var(--color-border-5) !important;
    border-width: 0.5px !important;
}

strong
{
    color: var(--color-text-5) !important;
}

::selection,
::-moz-selection
{
    background-color: rgb(237, 51, 102) !important;
}

.elementor-222229 .elementor-element.elementor-element-5fcd584f .elementor-image img
{
	filter: invert(100%);
}

.elementor-222229 .elementor-element.elementor-element-3bce968f
{
	--divider-border-color: #ffffff;
}

.elementor-222223 .elementor-element.elementor-element-7bcfaf21 > .elementor-element-populated
{
	border-color: var(--color-border-5) !important;
}

.elementor-widget-divider,
.elementor-222229 .elementor-element.elementor-element-3bce968f
{
	--divider-border-color: var(--color-border-5) !important;
}
}
