style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://userstyles.org/") {
	
	body {
		background-color: #202B33;
	}
	
	#nav-view, #navigation {
		background-color: #606c75;
	}
	
	#overlay-info {
		background-color: rgba(46, 62, 73, .98);
	}
	
	#images-carousel {
		border-radius: 5px;
	}
	
	.linksBox .navigation_link:last-child {
		box-shadow: unset;
	}
	
	.navigation_link:hover {
		background-color: #3498db;
		box-shadow: unset;
		cursor: pointer;
	}
	
	.navigation_title {
		color: #dfe1e3;
	}
	
	.style_title {
    	color: #dfe1e3;
	}
	
	
	#notes_textarea {
		background-color: #2e3e49;
		color: #dfe1e3;
		padding: 5px;
	}
	
	#login, #headline, #share_div, #information_key_left, #infomation_value_left, #information_key, #information_value, .text_style_page {
		color: #dfe1e3; 
	}
	
	#applies_link {
		color: #ffffff;
	}
	
	.style_card {
    	background-color: #2e3e49;
	}
	
	a {
		color: #45a6e7;
	}
	
	a:hover {
		color: #3498db;
	}


	h4 a {
		color: #ccced2 !important;
	}
	
	h4 a:hover {
    	color: #dfe1e3 !important
	}
	
	.advanced_button, .css_button, .white_button, #send_feedback_button {
		background-color: #2e3e49;
		border: unset;
		color: #8d9b9e;
	}
	
	.advanced_button:hover, .css_button:hover, .white_button:hover, #send_feedback_button:hover {
		background-color: #202b33;
	}

	
	.screenshot_wrapper {
		border-bottom: 1px solid #606c75;
	}

	.see_more {
		background-color: #2e3e49;
	}
	
	.see_more:hover {
		background-color: #202b33;
	}
	
	#footer {
    	background-color: #2e3e49;
	}
	
}
