style.css:
@-moz-document domain("nightbot.tv") {
.gray-bg {
    background-color: #45596C;
}
.navbar {
    background-color: #1E282E;
}
.border-bottom {
    border-bottom-color: #2F4050 !important;
}
div.wrapper:nth-child(1),
.footer,
.breadcrumb,
.ibox-content,
.ibox-title {
    background-color: #2F4050;
    border-color: #2F4050;
    color: #e8e9eb;
}

/* This is the little black bar on the white page style, needs to be the same as the navbar*/
#page-wrapper > div:nth-child(1) > div:nth-child(1) {
    border-bottom-color: #1E282E !important;
}
}
