style.css:
@-moz-document domain(web.whatsapp.com) {

.pane-header.pane-list-header {
    background-image: url(https://i.imgur.com/gQFObEI.jpg);
}
.pane-chat-header {
    border-left: 0px solid rgba(0, 0, 0, 0.08);
    background-image: url(https://i.imgur.com/5ejaF1i.jpg);
}
.pane-chat-header-subtitle {
    color: rgba(255, 255, 255, 0.6);
}
.menu-horizontal .menu-horizontal-item:first-of-type {
    background-image: url(https://i.imgur.com/OJETBKl.jpg);
}
.menu-horizontal .menu-horizontal-item {
    background-image: url(https://i.imgur.com/OJETBKl.jpg);
}
.chat-title,
.header-title {
    color: #fff;
}
.chat-subtitle.chat-subtitle-header {
    color: rgba(255, 255, 255, 0.6);
}
.list-search {
    background-color: #5475c2;
}
.list-search .input-search {
    background-color: #5475c2;
}
.chat {
    background-color: #5475c2;
}
.chatlist .chat.active {
    background-color: #0c3479;
}
.pane-chat-tile-container {
    background-image: url(https://archive-media-0.nyafuu.org/wg/image/1425/96/1425967737252.jpg);
    background-size: 100% 100%;
}
.bubble {
    background-color: #fcffd1;
}
.tail.message-out .tail-container {
    width: 0px;
    height: 0px;
}
.tail.message-in .tail-container {
    width: 0px;
    height: 0px;
}
.block-compose {
    background-image: url(https://i.imgur.com/5ejaF1i.jpg);
}
.block-compose .input-container {
    background-color: #ffffff;
    border-color: #0c3479;
}
.emoji-panel-body-container {
    background-color: #0c3479;
}
.emoji-search-row {
    background-color: #fff;
}
.menu-tabs-marker {
    background-color: #ffbc00;
}
.menu-tabs-emoji {
    background-color: #5475c2;
}
.drawer-header {
    background-image: url(https://i.imgur.com/pe4x2cN.jpg);
}
.drawer-body {
    background-color: #3253b3;
}
.hint,
.media-gallery-empty,
.well-chat-secondary,
.empty-text,
.image-gallery-footer-text,
.intro-text,
.intro-secondary-text,
.drawer-blocked-hint {
    color: #fff;
}
.dropdown,
.dropdown-picker {
    background: linear-gradient(to bottom, #ff8800 0%, #ff8800 2%, #ffffff 2%, #ffffff 100%);
}
.drawer-header-offset {
    background-color: #3253b3;
}
.media-collection {
    background-color: #0c3479;
}
.capture-footer {
    background-color: #3253b3;
}
.drawer-header-popup {
    background-color: #5475c2;
}
.list-search {
    background-color: #5475c2;
}
.list-search .input-search {
    background-color: #5475c2;
}
.section-header {
    background-color: #0c3479;
}
.cont-input-search {
    background-color: #5475c2;
    border: 1px solid #5475c2;
}
.input-placeholder {
    color: #fff;
}
.chatlist .chat:hover:after,
.chatlist .chat.hover:after,
.chatlist .chat.active:after {
    border-top: 1px solid #5475c2;
}
.chatlist .chat-body,
.chatlist .header-body {
    border-top: 1px solid #5475c2;
}
.pane-header {
    background-color: #3153b3;
}
.chatlist .chat:hover,
.chatlist .chat.hover {
    background: linear-gradient(to right, #ff8800 0%, #ff8800 2%, #5475c2 2%, #5475c2 100%);
}
.chatlist .chat:hover .chat-body,
.chatlist .chat:hover .header-body,
.chatlist .chat.hover .chat-body,
.chatlist .chat.hover .header-body {
    border-top-color: #5475c2;
}
.qr-wrapper:before {
    background-color: #3153b3;
}
.qr-wrapper {
    background-image: linear-gradient(to bottom, #3153b3, #3153b3);
}
.qr-window {
    margin: 0 auto;
    margin-top: 5%;
    margin-bottom: 20px;
    width: 100%;
    background: linear-gradient(to bottom, #ff8c00 0%, #ff8c00 3%, #7db9e8 3%, #7db9e8 97%, #ff8c00 97%, #ff8c00 100%);
}
.qr-platforms {
    background-color: transparent;
    margin-left: 13%;
}
.entry-main {
    margin: 68px 90px 64px 20%;
}
.intro {
    background-color: #0c3479;
}
.intro:after {
    border-top: 6px solid #ff9a00;
}
.intro a {
    color: #ff9a00;
}

}
