style.css:
@-moz-document domain(jeuxvideo.com)
{
  #prospect,
#prospect.show-pub,
#prospect #table-prospect,
.part-boutique, 
.bonus-track, 
.bonus-sponso,
.mdd-card-suggest, 
#jv-feedback .btn-feedback,
#prospect-square.show-pub, 
#prospect-square2.show-pub,
#prospect-square3.show-pub
  {
    display: none !important;
  }/* Supprime les pubs */
}

@-moz-document regexp('http://www.jeuxvideo.com/(?!profil)([^"]*)'), regexp('https://www.jeuxvideo.com/(?!profil)([^"]*)')
{
  #content
  {
    padding-top: 20px !important;
  }/* Ajoute un espace entre la barre du menu et la page */
}

@-moz-document url-prefix("http://www.jeuxvideo.com")
{
/* Background du site */

    #content
  {
    background-color: #000000 !important;
    background-image: url("https://image.noelshack.com/fichiers/2017/34/4/1503558487-bg.jpg") !important;
  }/* Modifie le background du site */
    
    #prospect-interstice
  {
    display: none !important;
  } /* Supprime la page "Acc√©der au site" */
}
