style.css:
@-moz-document domain("www.youtube.com") {
html:not([style-scope]):not(.style-scope){background:#000000 url(https://i.imgur.com/FNK4Oqh.jpg) no-repeat fixed right top!important;  }
ytd-page-manager > *.ytd-page-manager,ytd-watch,body {background: transparent !important;}
div#primary {
    background: transparent !important;
}
#columns.ytd-watch-flexy {
background: rgba(0, 0, 0, 0.7) !important;
}
#contents.ytd-channel-list-sub-menu-renderer {
    margin-left: 235px;
}

ytd-section-list-renderer.style-scope.ytd-two-column-search-results-renderer {
background: rgba(0, 0, 0, 0.7) !important;
}
ytd-two-column-browse-results-renderer.style-scope.ytd-browse {
    margin-top: 10px;
}
#guide-wrapper.ytd-app {
    background: transparent !important;
}
#guide-content.ytd-app {
background: rgba(0, 0, 0, 0.7) !important;
}

/*####----BROWSER SCROLL BAR----####*/
::-webkit-scrollbar {
background: -webkit-linear-gradient(40deg , #040404 , #0C0C0C , #040404 100%)!important;
border-left: 1px solid #1A1A1A !important;
}
::-webkit-scrollbar-thumb {background-color: #272727!important;border-radius: 16px!important;}
/*####----SELECT_DRAG----####*/
::selection {background: #000000;color: #FF0000;}
/*####----CHROME_AUTOFILL----####*/
input:-webkit-autofill {-webkit-box-shadow: 0 0 0px 1000px #272727 inset!important;-webkit-animation-name: autofill;-webkit-animation-fill-mode: both;border-color: #1A1A1A !important;}
@-webkit-keyframes autofill {to {color: #CACACA;border-color: #1A1A1A !important;}}

/*Background URL Pixel Colour*/
html {
    background-image: url(http://files.enjin.com/106810/searchthatengine/img/dotmatrix.png) !important;
     background: #000 !important;   
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
}

