style.css:
@-moz-document url-prefix("https://userstyles.org") {
#button_middle { order: 1 }
/*[[order]]*/

#headline,
#style_info > a,
#information_div,
.adContainer,
#applies_link,
.text_style_page,
#notes_textarea,
#as_userscript,
#discussions-area { 
	order: 4
}

#button_middle {
	margin-left: auto
}
.css_button, .advanced_button {
	margin-top: 10px;
	margin-bottom: 10px
}
.advanced_button {
	margin-left: 10px
}
.advancedsettings_shown {
	margin-bottom: 10px
}
#css_text_area_div, #style_info > div:nth-child(6) {
	margin-top: 0px
}
/*[[r]]*/
}
