style.css:
@-moz-document domain("justdancenow.com")
{
#lyrics::before {
    content: '';
    position: absolute;
    opacity: 0.35;
    height: 0%;
    width: 0.0%;
    left: 7.1%;
    bottom: -1%;
    background-color: white;
    transform: skew(12deg);
}
.state-interstitial #lyrics::before,
.state-loadingdance #lyrics::before,
.state-tutorial #lyrics::before {
    display: none
}
#lyrics .line.current,
#lyrics .line.previous {
    left: 0.1% !important;
}
#lyrics .line {
    left: 0.1%;
}
#beat {
    height: 94% !important;
    background-image: url(http://i.imgur.com/NSGoo8F.png) !important;
    background-repeat: no-repeat  !important;
    background-size: 85% 85% !important;
    background-position: bottom !important;
}
#beat .particle-system {
display: none;
}
html:not(.ipad) .hud.beat>#pictos>#beat {
    -webkit-animation-name: pictoOut !important;
    animation-name: pictoOut !important;
}
@keyframes pictoOut {
    0% {
        filter: brightness(9999%);
        background-size: 75% 0%;
        opacity: 1;
    }
    0.5% {
        background-size: 85% 85%;
    }
    100% {
        background-size: 90% 5%;
        opacity: 0;
    }

}
    #goldmove .layer.layer-1 {
    opacity: 0.8;
    background-image: url(https://vignette4.wikia.nocookie.net/justdance/images/d/d7/Just_Dance_2016_-_Gold_Move_-_NEW%21%21_-_FHD_1_00004.png/revision/latest?cb=20160623183536);
    background-repeat: round;
    filter: brightness(150%)
}
#goldmove .layer.layer-2 {
    opacity: 0.8;
    background-image: url(https://vignette4.wikia.nocookie.net/justdance/images/d/d7/Just_Dance_2016_-_Gold_Move_-_NEW%21%21_-_FHD_1_00004.png/revision/latest?cb=20160623183536);
    background-repeat: round;
    filter: brightness(150%)
}
#goldmove .layer.layer-3,
#goldmove .layer.layer-4 {
    background-image: url(https://vignette4.wikia.nocookie.net/justdance/images/d/d7/Just_Dance_2016_-_Gold_Move_-_NEW%21%21_-_FHD_1_00004.png/revision/latest?cb=20160623183536);
    background-repeat: round;
    filter: brightness(150%)
}
    /* ---------------- PLAYERS ---------------- */
    
    .state-dance #players {
        left: 10%!important;
        right: 10%!important;
        justify-content: space-around!important;
        width: auto;
        display: flex
    }
    .state-dance #players .player {
        position: static!important
    }
    .state-tutorial #players .player-color {
        visibility: visible;
        opacity: 1
    }
    #players .feedback-label {
        background-image: url(http://i.imgur.com/i91rVnK.png);
        background-repeat: no-repeat;
        top: 70%
    }
    #players .feedlabel-ok {
        background-position: 0 59%
    }
    /* ---------------- RACETRACK ---------------- */														
    /* ---------------- DANCE_TRANSITION ---------------- */
    
    #dance-transition {
        z-index: 9999999999999999999
    }
    #dance-transition .dt1 {
        background-color: #fff;
        top: 0!important
    }
    #dance-transition.animate.in .dt-row {
        -webkit-transition-property: opacity!important;
        transition-property: opacity!important
    }
    #dance-transition::before {
        background: url(http://i.imgur.com/hNcNwmJ.png) center no-repeat, center no-repeat #fff!important;
        background-size: cover!important;
        width: 100%;
        height: 100%;
        z-index: 1
    }
    /* ---------------- RACETRACK ---------------- */
    
    #racetrack {
        top: 22%;
        width: 9.7%;
        height: 55.2%
 }
    #racetrack .platform {
        width: 0%;
        float: right;
        opacity: 0;
    }
    [data-stars="1"] .feedback-label,
    [data-stars="2"] .feedback-label,
    [data-stars="3"] .feedback-label,
    [data-stars="4"] .feedback-label,
    [data-stars="5"] .feedback-label {
        top: 83% !important;
    }
    
    .state-dance #players .player {
        position: static!important
    }
    .state-tutorial #players .player-color {
        visibility: visible;
        opacity: 1
    }

#lyrics::before {
    content: '';
    position: absolute;
    opacity: 0.35;
    height: 100%;
    width: 0.3%;
    left: -2%;
    bottom: 1%;
    background-color: white;
    transform: skew(12deg);
    }
    #racetrack .star {
        left: 37.5%;
        width: .48rem;
        height: .48rem;
        z-index: 99
    }
    /* ---------------- ROOM_INFO ---------------- */
    
    .state-dance.buffering .fullscreen-spinner {
        pointer-events: none;
    }
    .state-dance #room-info,
    .state-dance .toggle-fullscreen,
    .state-dance .toggle-settings {
        opacity: .4!important;
        z-index: 9999999!important
    }
    .state-dance #room-info .account-name,
    .state-dance #room-info .account-name .icon,
    .state-dance #room-info .room-number .icon,
    .state-dance .danceroom__qr-code-wrapper,
    .state-dance #just-dance-now .danceroom__label {
        display: none
    }
    .state-dance #room-info .room-number {
        font-size: 0%;
        text-shadow: none;
        color: none
    }
    .state-dance #room-info .room-number span.number {
        margin-left: 0;
        margin-right: 0%
    }
    .state-dance #room-info {
        display: block;
        top: 0%;
        left: 0%;
        opacity: .0;
        width: 0%;
        text-align: none;
        z-index: 0
    }}
