Author: JeanPaulLucien
Version: 1.0
Home Page: https://userstyles.org/users/364674
Goal URL: https://www.linkedin.com/post/new|edit
*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.linkedin.com/post/new"), url-prefix("https://www.linkedin.com/post/edit") {
	
	html {
		background: #030611;
	}
  
  .cover-image {
	height: 150px;
}
  .cover-image__label {
	background-position: center -40px;
	margin: 0;
}
	.post-headline {
	background: white;
	padding: 0 12px;
	box-shadow: inset 0 0 2px black;
}
	.post-editor .ql-editor {
	padding: 0px 10px;
	box-shadow: inset 0 0 2px black;
	background: #050918;
	border-radius: 2px;
	transition: all 500ms ease 0s;
	color: #737679 !important;
}
	.post-editor .ql-editor:hover, .post-editor .ql-editor:focus {
		background: #D9D9D9;
	}
	.post-editor .ql-editor p {
		background: transparent;
		color: #737679;
	}

}
