style.css:
@-moz-document regexp("http(s?)://(www.|)(next.|)angular.io(.*)") {
md-sidenav.mat-sidenav.sidenav.collapsed,md-sidenav.mat-sidenav.sidenav,.toc-container,.form-select-button,.mat-sidenav-container,.l-sub-section,.alert.is-critical, .is-critical.callout,code-example:not(.no-box),code-tabs md-tab-body:not(.no-box),.pln,.callout.is-important,.alert.is-important,.is-important.callout,li>code,p>code,table code,.home-row .card,.home-row .card .card-text-container p, a, input, li, ol, p, ul, .card-container .docs-card section, .card-container .docs-card p, h1, h2, h3, h4, h5, h6 {
    color: #ddd;
}

.content, body,md-sidenav.mat-sidenav.sidenav.collapsed,md-sidenav.mat-sidenav.sidenav,.toc-container,.l-sub-section,.alert.is-critical,.is-critical.callout,code-example:not(.no-box),code-tabs md-tab-body:not(.no-box),li>code,p>code,table code,.filetree,table,table tbody th,body,.alert.is-helpful,.is-helpful.callout,.callout.is-important,.alert.is-important,.is-important.callout,footer,.home-row .card,.announcement-bar,.content figure, mat-sidenav.mat-sidenav.sidenav,code-example:not(.no-box), code-tabs mat-tab-body:not(.no-box), mat-sidenav-container .sidenav-content, mat-sidenav-container.sidenav-container.mat-drawer-container.mat-sidenav-container {
    background: #000;
}

.vertical-menu-item,.toc-inner ul.toc-list li a,.alert, .callout{color: #999;}

.vertical-menu-item:hover,.toc-inner ul.toc-list li:hover a{color: #abd5ff;}

.form-select-button,.mat-sidenav-container, .callout.is-helpful, .card-container .docs-card, .alert.is-helpful {background: #1b1b1b;}

.form-select-dropdown li,.vertical-menu-item:hover,.toc-inner ul.toc-list li:hover a{background: #333;}

.form-select-dropdown li.selected{color: #000;}

.sidenav-content a,.mat-tab-label,.mat-tab-link{color: #69b4ff;}

.lit,.kwd,.tag {color: #05acff;}

.com{color: #00d000;}
.pun{color: #bbb;}
.atn{color: #fe58ff;}
.str,.atv{color: orange;}

code-example header {background-color: #003666;}

li>code,p>code,table code,.filetree {border: 1px solid #999;}

.filetree,table {border-radius: 5px;}

.filetree .file{color: #ddd;}

table {overflow: hidden;}

table tbody td, table tbody th, table tbody tr:last-child td {border-right: 1px solid #fff;}

.mat-toolbar.mat-primary {background: #00264c;}

.callout.is-important header {background: rgba(255, 131, 0, 0.58);}

.sidenav-content a, .mat-tab-label, .mat-tab-link {color: #4da3ff;}

.mat-tab-label, .mat-tab-label[_ngcontent-c5]{opacity: 1 !important;}

.syntax {color: rgb(0, 255, 245);}

.background-sky {background-color: #1976d2;background: linear-gradient(145deg,#003d9c,#001221);}

img, .content figure img { opacity: 0.7 !important;}

.marketing-banner {background-color: #003265;}
}
