style.css:
@-moz-document domain("my.nintendo.com") {
.NHeader, .NFooter, .NewsItem_content, .MainNav_button, .NotificationItem-readed, .SiteNavPanelMenu-main .SiteNavPanelMenu_item, .SiteNavPanelMenu-sub, .SiteNavPanel_contentScroller, .AboutPoint_expiration .callout, .Pager_item-first:after, .Pager_item-next:after, .Pager_item-previous:after, .Pager_item>a:hover, .SecondaryNav_item.current, .PointCategory_label .valueCell:before, .PointCategory_child, .PointExpiration_expirePoints:before, .PointHistoryGroup_points:before, .form-select .select:hover, .PointDetailChild, .RewardSwipe_info, .MissionItem_points, .btn-inverse:hover, .MissionItem_progressBar, .RewardPoint_wallet, .RewardItem_info, .MoreRewards_end .target:hover, .RewardCategoryList_heading, .RewardDetail_officialButton:hover, .AnonymousIndex_header .signInButton:hover, .AnonymousIndex_header .signUpButton:hover, .GiftItem_points, .OthersItem .OthersItem_visual-step .step {
    background-color: #202020 !important;
}
.Layout-main, .MiiStage, .PrimaryContent, .SecondaryContent, .MiiStage_bg, .MFooter, .Apps_inner, .AboutPoint_section, .AboutPoint_caution, .Layout-app, .GettingStartedPoint_reward, .SerialNumber_footer, .SerialNumber_enclose, .SerialNumber_actionInput, .SerialNumber_preview span.disabled, .Pager, .SecondaryNav, .PointCategory, .PointExpiration_item, .PointHistoryGroup_summary, .form-select, .PointHistoryGroup_footer, .PointHistoryGroup_border-expand, .PointDetail_body, .RewardSwipe, .RewardSwipe_title .text:before, .RewardFilter, .SiteNavPanelMenu-main .SiteNavPanelMenu_item:hover, .DirectNav a, .MissionItem_container, .btn-inverse, .Modal_contentInner, .RewardDetail_container, .RewardPoint, .MoreRewards, .RewardItem_title:before, .AvailablePoint_container, .RewardList_inner, .NewsDetail_body, .NewsDetail_footer, .AnonymousIndex_header, .AnonymousIndex_header .signInButton, .AnonymousIndex_header .signUpButton, .GiftList, .RewardResult, .GPRate, .GPGetType_body, .GPCaution, .GPFooter, .RewardGold {
    background-color: #161616 !important;
}
.Layout-app, .Modal_contentInner, .AnonymousIndex_header .signUpButton {
    background-image: none  !important;
}
.Apps_inner, .AboutPoint_section, .AboutPoint_caution, .GettingStartedPoint_reward, .SerialNumber_enclose, .SerialNumber_actionInput, .Pager, .SecondaryNav, .PointCategory, .PointExpiration_item, .PointHistoryGroup_summary:only-child, .form-select, .form-select:before, .RewardFilter, .DirectNav a, .MissionItem_container, .RewardDetail_enclose, .AvailablePoint_container, .AnonymousIndex_header .signInButton, .AnonymousIndex_header .signUpButton, .GPCaution {
    border: 4px solid #202020 !important;
}
.RewardPoint, .RewardPoint_wallet, .GiftList, .RewardResult, .GPRate, .RewardGold {
    border: 8px solid #202020 !important;
}
.NotificationItem:last-child {
    border-bottom: 1px solid #202020 !important;
}
.SiteNavPanelMenu-main .SiteNavPanelMenu_item+.SiteNavPanelMenu_item, .SiteNavPanelMenu-sub {
    border-top: 1px solid #161616 !important;
}
.PointHistoryGroup_summary, .PointHistoryGroup_footer {
    border-color: #202020 !important;
}
.SecondaryNav_itemSlopeLeft {
    background-image: none !important;
    border-right: 1px solid #202020 !important;
}
.PointHistoryGroup_border {
    border-right: 8px solid #202020 !important;
    border-left: 8px solid #202020 !important;
}
.SecondaryNav_itemSlopeRight {
    background-image: none !important;
    border-left: 1px solid #202020 !important;
}
.OthersItem~.OthersItem, .AnonymousIndex_pointSection {
    border-top: 8px solid #202020 !important;
}
body, .NewsItem_title, .SiteNavPanelMenu-main .mynintendo-icons, .SiteNavPanelMenu-main .label, .AboutPoint_expiration p,.AboutPoint_expiration .callout dt, .AboutPoint_expiration .callout dd, .AboutPoint_caution .title, .AboutPoint_caution .description p:last-child, .AboutPoint_pointDescription .text, .OthersItem_content .description, .GettingStartedPoint_description, .GettingStartedPoint_header .subTitle, .SerialNumber_actionInput, .SerialNumber_message, .PointHistoryGroup_title, .RewardSwipe_title .text, .MissionItem_title, .MissionHelp_missionDescription, .MissionHelp_missionTitle, .RewardDetail_subtitle, .RewardDetail_description, .RewardLimit_title, .RewardDetail_title, .RewardPoint_main .pointName, .RewardDetail_pointHeader, .RewardItem_title, .RewardCategoryList_heading, .mynintendo-icons-reward:before, .NewsDetail_title, .NewsDetail_body p, .RewardDetail_officialButton, .AnonymousIndex_videoContent .description, table, th, td, b, .GiftItem_title, .RewardResult_header .title, .RewardResult_header .lead {
   color: #828282 !important;
}
.MainNav_button {
    box-shadow: 0 4px 0 0 #121212 !important;
}
.MainNav_line line, .PointHistoryGroup_border .dot line, .MissionHelp_line line, .RewardDetail_container .RewardDetail_line line, .AvailablePoint_divider line, .RewardResult_line line {
    stroke: #202020 !important;
}
.RewardItem_line line {
    stroke: #161616 !important;
}
.PointCategory_child .tongue path, .PointDetailChild .tongue path {
    fill: #202020 !important;
}
.Tag-coming-soon {
    background-color: #e60012 !important;
    color: #ffffff !important;
}
.MissionBulkReceive {
    border-top: 4px solid #171717 !important;
    background-color: #232323 !important;
}
.MissionItem_progressBar .division {
    background-color: #3e3e3e !important;
}
.MFooter_character .mario {
    background-image: url(/*[[character]]*/) !important;
}
small.copyright:after {
    content: ' - Dark My Nintendo v1.2.6 by CodyMKW' !important;
}
}
