style.css:
@-moz-document domain("www.youtube.com") {
/* Programmed by: Orlando Trejo -- orlandotrejo [at] usb [dot] ve
   October 2017. Currently Under Development
   version 0.4*/

html:not([style-scope]):not(.style-scope) {
    --yt-swatch-primary: #767676 !important;
}

*:not([style-scope]):not(.style-scope) {
    --yt-primary-color: #ccc !important;
    --yt-hovered-text-color: #369eec !important;
    --yt-main-app-background: hsl(0, 0%, 6.7%) !important;
    --yt-guide-background: rgba(0, 0, 0, 0.8) !important;
    --yt-dialog-background: #ddd !important;
    --yt-searchbox-background: hsla(0, 100%, 100%, .88) !important;
    --yt-border-color: hsl(3, 81.8%, 49.6%) !important;
    --yt-button-text-color: hsl(0, 0%, 53.3%) !important;
    --yt-border-color: hsla(3, 81%, 36%, 0.66) !important;
}

*:not([style-scope]):not(.style-scope) {
    yt-metadata-color: #f00 !important;
}

a.yt-simple-endpoint.yt-formatted-string {
    color: #ddd;
}

#metadata-line.ytd-grid-video-renderer {

    color: #767676 !important;

}

ytd-page-manager > *.ytd-page-manager {
    
    background-color: hsl(0, 0%, 6.7%) !important;    
    
}

#content-text.ytd-comment-renderer {
    
    color: #767676 !important;
    
}

#author-text.yt-simple-endpoint.ytd-comment-renderer {
    
    color: #fff !important;
    
}

.title.ytd-video-primary-info-renderer {

    color: #fff;

}


span.yt-view-count-renderer {

    color: #fff;

}

ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer {

    color: #ddd;

}

#byline.ytd-video-meta-block {

    color: #ddd;

}

#video-title.ytd-compact-video-renderer {

    color: #fff;

}

#metadata-line.ytd-video-meta-block span.ytd-video-meta-block {

    color: #7d7d7d;

}

.description.ytd-video-secondary-info-renderer yt-formatted-string.ytd-video-secondary-info-renderer {

    color: #ddd;

}


#video-title.ytd-compact-radio-renderer {

    color: #fff;

}

ytd-button-renderer.force-icon-button a.ytd-button-renderer {

    color: #ddd;

}

.count-text.ytd-comments-header-renderer{

    color: #7d7d7d;

}

ytd-toggle-button-renderer #button.ytd-toggle-button-renderer {

    color: #7d7d7d;

}

#simplebox-placeholder.ytd-comment-simplebox-renderer{

    color: #fff;

}

#button.ytd-menu-renderer yt-icon.ytd-menu-renderer{

    color: #ddd;

}

ytd-button-renderer #button.ytd-button-renderer {

    color: #ddd;

}

.more-button.ytd-comment-renderer, .less-button.ytd-comment-renderer {
    
    color: #f66666;
    
}

.badge-style-type-live-now.ytd-badge-supported-renderer {
    
    background: transparent;
    
}

yt-icon.ytd-badge-supported-renderer{
    
    color: #48aaaa;
    
}

yt-formatted-string.ytd-guide-signin-promo-renderer {

    color: #ddd;
    
}

.input-content.paper-input-container  textarea {
 
    color: #ddd;
    
}

#zero-step-footer-text.ytd-commentbox {

    color: #ddd;

}

/*************** AquÃ­ para ver el estilo en la vista de canales ************/

#message.ytd-message-renderer {

    color: #7d7d7d;

}

#label-text.yt-dropdown-menu {

    color: #ddd;

}

#channel-header.ytd-c4-tabbed-header-renderer {

    background-color: #111;

}

#channel-title.ytd-c4-tabbed-header-renderer {

    color: #fdfdfd;

}

#subscriber-count.ytd-c4-tabbed-header-renderer {

    color: #ddd;

}

#title.ytd-vertical-channel-section-renderer {

    color: #fdfdfd;

}

.title.ytd-mini-channel-renderer {

    color: #ddd;

}

#video-title.ytd-grid-playlist-renderer {

    color: #fdfdfd;

}

#tabsContent.paper-tabs  > *:not(#selectionBar) {

    background-color: #ddd;

}

#tabs-inner-container.ytd-c4-tabbed-header-renderer {

    background-color: #ddd;

}

yt-formatted-string.content.ytd-channel-video-player-renderer {
    
    color: #ddd;
    
}

#subtitle.ytd-shelf-renderer {

    color: #ddd;

}

/******************* Aqui de vuelta a la vista del video ******************/

#video-title.ytd-video-renderer {

    color: #fff;

}

button[is="paper-icon-button-light"].ytd-button-renderer > yt-icon.ytd-button-renderer {

    color: #fff;

}

button[is="paper-icon-button-light"].ytd-toggle-button-renderer > yt-icon.ytd-toggle-button-renderer {

    color: #767676;

}

#result-count.ytd-search-sub-menu-renderer {

    color: #ddd;

}

.date.ytd-video-secondary-info-renderer {
    
    color: #fff;
    
}

/* Arreglar el recolor del theater view */

ytd-masthead[mode="legacy"] #container.ytd-masthead {

    background-color: #767676;

}

/* Estilo de los Mix de Youtube */

#video-title.ytd-playlist-renderer {

    color: #fff;

}

a.yt-simple-endpoint.yt-formatted-string {

    color: #7d7d7d;

}

#index.ytd-playlist-panel-video-renderer {

    color: #7d7d7d;

}

/* Latest Update October */

html:not([style-scope]):not(.style-scope):not([color-exp]) *, html[color-exp]:not([style-scope]):not(.style-scope){
	
    --yt-guide-background: #333;
    --yt-primary-text-color: hsl(0, 0%, 93.3%);
    --yt-primary-color: hsl(0, 0%, 93.3%);
    
}

}
