@-moz-document domain(cuspide.com) {
header {
position:relative;
}
header .cuspide:before {
    display: block;
    content: "";
    background: #F00;
    width: 500px;
    height: 95px;
    position: absolute;
    left: 289px;
    background: url(http://www.juanmartinsg.com/images/foto1.jpg);
    background-size: auto 100%;
    background-repeat: no-repeat;
}
header .cuspide:after {
    display: block;
    content: "";
    background: #F00;
    width: 500px;
    height: 95px;
    position: absolute;
    right: -79px;
    background: url(http://www.juanmartinsg.com/images/foto1.jpg);
    background-size: auto 100%;
    background-repeat: no-repeat;
    top: 38px;
}
.sp-slide:nth-child(1):before {
    width: 100%;
    height: 100%;
    background: url(http://www.juanmartinsg.com/images/foto1.jpg);
    z-index: 100;
    position: absolute;
    content: "";
    background-size: auto 100%;
}
.sp-slide:nth-child(2):before {
    width: 100%;
    height: 100%;
    background: url(http://www.juanmartinsg.com/images/foto2.jpg);
    z-index: 100;
    position: absolute;
    content: "";
    background-size: auto 100%;
}
.sp-slide:nth-child(3):before {
    width: 100%;
    height: 100%;
    background: url(http://www.juanmartinsg.com/images/foto3.jpg);
    z-index: 100;
    position: absolute;
    content: "";
    background-size: auto 100%;
}
}
