style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pikabu.ru") {
  #wrap {
    max-width: 100%;
  }
  .main-b {
    max-width: 100%;
  }
  .inner_wrap {
    max-width: 100%;
  }
  table.inner_wrap {
    width: 90%;
    max-width: 100%;
    margin-left: 80px;
  }
  .inner_wrap_content {
    width: 100%;
  }
  .story__main {
    max-width: 100%;
  }
  .page-story {
    margin-left: 80px;
  }
  .b-story-blocks {
    width: 90%;
  }
  .b-story__content b-story-blocks b-story-blocks_with-border {
    width: 90%;
  }
  .b-story__content_type_text {
    width: 90%;
  }
  .b-comments_type_main {
    width: 90%;
  }
  .b-comments_type_main {
    max-width: 90%;
  }
  .b-story-blocks {
    font-size: 15px;
  }
  .b-story__content_type_text {
    font-size: 15px;
  }
  .b-comment__content p {
    font-size: 15px !important;
  }
  .b-comment__content {
    font-size: 15px !important;
  }
}
