style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/*================================================================

  'Dark Redditbooru' theme for Stylish

  v0.3
  Last updated: 2017/09/07

================================================================*/

@-moz-document domain("redditbooru.com") {

  body {
    background-color: black;
  }
  
  #sources {
    background-color: darkgray;
  }
  
  .filters {
    color: grey;
    background-color: black;
  }
  
  .textSearch input {
    background-color: black;
    color: white;
  }
  
  #content #title {
    color: lightgrey;
  }
  
  .userProfile {
    color: lightgrey;
  }
}
