/* Code comment thread comment */

.comment-thread-container .comment,
.comment-thread-container .comments-list,
.comments-list .comment .author-lozenge {
    background-color: #222 !important;
    border-color: #333 !important;
}
/* Diff container */

.tabs-pane,
.diff-container .heading,
.diff-container .refract-container,
section h1 {
    border-color: #444 !important;
}
/* Main page area */

.aui-page-panel {
    background-color: #222 !important;
    color: #ddd !important;
}
/* Links */

a {
    color: #78A4D0 !important;
}
/* Left sidebar */

.aui-sidebar-wrapper {
    background-color: #333 !important;
    color: #ddd !important;
    border-color: #444 !important;
}
.aui-nav-selected a {
    background-color: #333 !important;
}
/* Page right side container */

.aui-page-panel-inner,
.aui-page-panel-inner h1,
.aui-page-panel-inner dt,
#repo-content,
.participants li {
    background-color: #222 !important;
    color: #ddd !important;
    border-color: #222;
}
/* Branch info */

.compare-widget-container,
.compare-widget-container .compare-widget-item {
    background-color: #222 !important;
    color: #ddd !important;
    border-color: #444 !important;
}
.compare-widget-container .compare-widget-item dd {
    background-color: #333 !important;
    color: #ddd !important;
    border-color: #444 !important;
}
/* Tabs */

.aui-tabs.horizontal-tabs>.tabs-menu>.menu-item {
    background-color: #333 !important;
}
/* Tab items */

.tabs-menu .menu-item a {
    background-color: #333 !important;
    color: #ddd !important;
    border-color: #444 !important;
    border-bottom-color: #444 !important;
}
/* Active tab */

.aui-tabs.horizontal-tabs>.tabs-menu>.menu-item.active-tab,
.aui-tabs.horizontal-tabs>.tabs-menu>.menu-item.active-tab a {
    background-color: #222 !important;
    color: #ddd !important;
    border-bottom: 1px solid #222 !important;
}
/* Buttons */

.aui-button,
.aui-button a,
.aui-buttons button span,
.aui-buttons a span,
.aui-buttons .approve {
    background-color: #333 !important;
    color: #ddd !important;
    border-color: #444 !important;
}
/* Approvals indicator */

.approvals {
    background-color: #333 !important;
    border-color: #444 !important;
}
.approvals:before,
.approvals:after {
    border-right-color: #444 !important;
}
/* Primary button */

.aui-button.aui-button-primary,
.aui-button.aui-button-primary span {
    background-color: #3572b0 !important;
}
/* Disabled button */

.aui-buttons span {
    background-color: #222 !important;
    color: #555 !important;
}
/* Reviewer avatar */

.aui-avatar-inner img {
    border: 1px solid #333 !important;
}
/* Comments indicator */

.count-badge,
.count-badge span,
.count-badge a {
    background: #222 !important;
    color: #ddd !important;
}
/* Comment editor */

.markItUpContainer,
.markItUpHeader {
    background-color: #333 !important;
    border-color: #444;
}
/* Comment editor toolbar */

.markItUp li,
li.markItUpButton a {
    background-color: #444 !important;
    border-color: #333 !important;
}
/* Comment editor edit area */

.markItUpContainer textarea,
.bb-mention-input {
    background-color: #000 !important;
    border-color: #444;
    color: #ccc !important;
}
/* Wiki content and sidebar headers */

.wiki-content h2,
.sidebar h2 {
    color: #ddd !important;
}
/* Newsfeed link */

.newsfeed a {
    color: #bbb !important;
}
/* Wiki content code block, git url */

.wiki-content pre,
.aui-buttons input {
    background-color: #000 !important;
    border-color: #444;
    color: #ccc !important;
}
/* Pull requests list */

#list-pullrequests tbody tr {
    background-color: #333 !important;
    color: #ddd !important;
    border-color: #444 !important;
}
/* Source code overview */

.aui-table-interactive tr {
    background-color: #333 !important;
    color: #ddd !important;
    border-color: #444 !important;
}
/* Source code view */

#source-view pre {
    background-color: #222 !important;
    border-color: #444;
    /*color: #ddd !important;	*/
    
    ;
}
/* Header */

.aui-header {
    background: #333;
}
/* Footer */

#footer,
#footer-logo,
#footer .footer-body {
    background-color: #222 !important;
    color: #ddd !important;
}
header.app-header h1 {
    color: #78A4D0 !important;
}
.aui-avatar-project {
    background: none !important;
}
div.file-source, div.file-source-container .toolbar, table, tr, td, .highlighttable td.annotations, .highlighttable td.linenos {
    border-color: rgba(255,255,255,0.2) !important;
}
#source-view div.file-source pre {
    background-color: rgba(0,0,0,0) !important;
}
span.kr {
    color: rgb(92, 125, 158) !important;
}
span.k {
    color: rgb(166, 117, 255) !important;
}
span.o {
    color: rgb(255, 140, 82) !important;
}
span.nb {
    color: rgba(255, 255, 0, 0.8) !important;
}
.iYJWvo {
	background: none !important;
}
}
