style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.torrents9.bz") {
  
  /* Arriere plan du site */
  .content-inner-section {
    background: #2d2d2d;
  }
  
  /* Arriere plan du corps */
  .content-inner {
    background: #2d2d2d;
  }
  
   /* Arriere plan ligne torrents */
  .table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #333;
  }
  
  /* Arriere plan Chemin catégories */
  .breadcrumb {
    background: #2d2d2d;
  }

  /* Texte torrents */
  .table-bordered.cust-table > tbody > tr > td > a {
    color: #dadada!important;
  }
  
  /* Taille torrents */
  .table-bordered.cust-table > tbody > tr > td {
    color: #dadada;
  }
  
  /* Hover sur les lignes des torrents */
  .table-striped > tbody > tr:hover {
    background-color: #4e4e4e;
  }
  
  /* Champ de recherche */
  .form-control {
    background-color: #2d2d2d;
  }
  .form-control.top-main-search {
    color: #dadada;
  }
  
  /* Torrents au hasard */
  .top-links-area {
    background-color: #2d2d2d!important;
    border: 1px solid #2d2d2d;
  }
  .top-links-area a {
    color: #dadada!important;
  }
  
  /* Catégories : Films, Séries TV... */
  .left-tab-section .nav-tabs li a {
    background: #454545;
    color: #dadada;
  }
  
  /* Texte : nom fichier, description */
  body {
    color: #dadada;
  }
  
  /* Texte : poids, date d'ajout */
  .movie-information ul li:last-child {
    color: #dadada;
  }
  
  /* Bloc avec liens de téléchargement */
  .movie-detail .row {
    background-color: #2d2d2d!important;
  }
  
  /* Bloc "Trier" */
  #trier div {
    background: #2d2d2d!important;
  }
  legend {
    color: #dadada;
  }
  #trier a {
    color: #337ab7!important;
  }
  
  /* Top Torrents - Bloc "Filtres" */
  .left-tab-section div {
    background: #2d2d2d!important;
  }
}
