style.css:
@-moz-document url-prefix("http://asu.tti.sfedu.ru/Raspisanie/ShowRaspisanie.aspx") {
body {
    background: #000;
}
.title_center {
    color: #00f900;
    font-size: 20pt;
    font-weight: normal;
    text-decoration: none;
    /* text-align: center; */
}
.group-name {
    padding: 2px 5px;
    background: #000;
    font-weight: bolder !important;
    border-radius: 5px;
    color: #00f900;
}
td.th_row {
    color: #00f900 !important;
    font-size: 8pt;
    font-weight: bolder !important;
    font-weight: lighter;
    background-color: #000;
    border: solid 1px #00f900;
    text-align: center;
    height: 30px;
    vertical-align: middle;
    font-family: "Segoe UI Regular", "Verdana", "Helvetica", sans-serif !important;
}
td.th_row_day {
    font-size: 10pt;
    font-weight: bolder !important;
    text-transform: uppercase;
    background-color: #000;
    color: #00f900 !important;
    border: 1px #00f900 solid;
    text-align: center;
    vertical-align: middle;
    font-family: "Segoe UI Regular", "Verdana", "Helvetica", sans-serif !important;
}
td.row_rowspan {
    color: #00f900;
    text-align: center;
    border: 1px #00f900 solid;
    height: 60px;
    vertical-align: middle;
    transition: 0.3s ease;
    font-family: "Segoe UI Regular", "Verdana", "Helvetica", sans-serif !important;
}
td.row {
    color: #00f900;
    text-align: center;
    border: solid 1px #00f900;
    height: 30px;
    vertical-align: middle;
    transition: 0.3s ease;
    font-family: "Segoe UI Regular", "Verdana", "Helvetica", sans-serif !important;
}
}
