style.css:
@-moz-document domain("keep.google.com") {
body{
    
    background: url(http://hdwallpaperbackgrounds.net/wp-content/uploads/2015/09/amazing-retro-wallpapers.jpg) no-repeat center center fixed;
    background-color: #222222;
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
	
}

/*Header*/
header .gb_ud.gb_0d.gb_1d{
    background-color: rgba(218,165,32,.8) !important;
}

header .gb_qe.gb_dc.gb_g{
    overflow: visible;
}

/*google search bar*/
.IZ65Hb-TBnied.HLvlvd-h1U9Be{
    background-color: rgba(255,255,255,.5) !important;
}
.notranslate.IZ65Hb-YPqjbf.r4nke-YPqjbf[contenteditable=true]{
    background-color: rgba(218,165,32,.8) !important;
}

.h1U9Be-xhiy4.qAWA2{
    width: 600px;
    position: relative;
    left: 50%;
    transform: translateX(-60%);
    margin-top: 50px;
}
.h1U9Be-xhiy4{
    margin: 0;
}

.neVct-LS81yb-tJHJj{
    color: rgba(255,255,255,.5);
}
/*Left Side Navigation Bar*/
.gb_hc{
    background-color: rgba(0,0,0, .5);
    box-shadow: 0 0 15px 3px rgba(0,0,0,.3);
    position: absolute;
}

.gb_hc li:hover{
    background-color: rgba(0,0,0,.7);
}
.gb_hc li a{
    color: rgba(255, 255, 255, .5);
}
.gb_hc li a svg{
    color: rgba(255, 255, 255, .7);
}

.gb_hc li:hover > a, .gb_hc li:hover > a svg{
    color: goldenrod !important;
}

/*notes container*/
.notes-container .gkA7Yd-sKfxWe{
    margin-left: 10px;
  
}

/*Cards*/
.IZ65Hb-n0tgWb.IZ65Hb-WsjYwc-nUpftc{ /*Non pinned card*/
    background-color: rgba(255,255,255,.8);
}
.IZ65Hb-TBnied{ /*pinned card*/
    background-color: rgba(255,255,255,.8);
    height: 100px;
}

/*hovering over a card*/
.IZ65Hb-n0tgWb.IZ65Hb-WsjYwc-nUpftc:hover, .IZ65Hb-TBnied:hover{
    background-color: blue;
    height: auto;
    z-index: 1000;
}
.IZ65Hb-n0tgWb.IZ65Hb-WsjYwc-nUpftc:hover > .IZ65Hb-TBnied.HLvlvd-h1U9Be, .IZ65Hb-TBnied:hover > .IZ65Hb-TBnied.HLvlvd-h1U9Be{
    height: auto !important;
    z-index: 1000;
}

/*Cards expanded view - Selectable card popup*/
.IZ65Hb-n0tgWb.IZ65Hb-bJ69tf.IZ65Hb-QQhtn.oT9UPb > .IZ65Hb-TBnied.HLvlvd-h1U9Be{ /*pinned card*/
    height: auto;
}
.IZ65Hb-n0tgWb.oT9UPb.IZ65Hb-QQhtn > .IZ65Hb-TBnied.HLvlvd-h1U9Be{ /*non pinned card*/
    height: auto;
}

/*Labels and edit button in the left side naviation bar*/
.gb_jc .JNdkSc-tJHJj, .gb_jc div[role=button]{
    color: rgba(255,255,255,.5);
}

.gb_jc div[role=button]:hover{
    background-color: goldenrod !important;
}

/* open source .hSRGPd-haAclf */
ul[id="gb$:e"]{
    display: none; /*hide terms of use, open source panel*/
}
header[role="banner"]:hover{
    background-color: red;
}
header[role="banner"]:hover ul[id="gb$:e"]{
    background-color: darkorange !important; /*show the terms of use, open source panel*/
    display: block;
}

/*Upon hovering over the left menu - menu items > hide the privacy and terms of use panel*/
.gb_gc :hover ~ .gb_ic ul[id="gb$:e"]{
    display: none;
}
/*End Upon hovering over the left menu - menu items > hide the privacy and terms of use panel*/

.gb_ic{
    position: relative;
}
.gb_ic ul{
    background-color: rgba(218,165,32,1); /*goldenrod*/
    border-radius: 0 0 5px 5px;
	position: absolute;
    left: 30%;                      
}
.gb_ic ul div {
    background-color: transparent;
    margin: 0;
}
.gb_ic ul div a {
    color: black !important;
}
}
