style.css:
@-moz-document domain("yoraikun.wordpress.com") {
/* 
* Author: Ew0345
* Version: 1.2
*/

/* Body */
body {
    background-color: #1f1f1f !important;
}
/* Header */
a[rel="home"],
#site-description {
    color: #eee !important;
}
/* Main Site Area */
.hfeed {
    background-color: #232323 !important;
}
.entry-title a:link,
.entry-title a:visited,
.entry-title {
    color: lightslategray !important;
    font-style: italic !important;
}
.entry-title a:hover {
    color: red !important;
}
.entry-content {
    color: #eee !important;
    font-family: 'Open Sans', Helvetica, sans-serif !important;
}
.entry-meta {
    color: #aaa;
}
#entry-author-info {
    background-color: #1f1f1f !important;
    color: #eee !important;
}
#author-description h2 {
    color: lightslategray !important;
    font-style: italic !important;
}
.sd-title {
    color: #eee !important;
}

/* Calendar Widget */
#calendar-2 {
    display: none !important;
}
/* Comments Area */
#comments h3 {
    color: lightslategray !important;
}
.says,
.comment-body {
    color: #eee !important;
}
.commentlist .bypostauthor > div {
    background-color: #1f1f1f !important;
}
.commentlist cite,
.commentlist cite a:link,
.commentlist cite a:visited {
    color: #eee !important;
}
/* Follow Blog Button */
#actionbar {
    display: none !important;
}
/* Captioned things? */
.wp-caption {
   background: #303030 !important;
}
.wp-caption-text {
    color: #ccc !important;
}
}
