style.css:
@-moz-document domain("inbox.google.com") {
.b4, .b4 .at, .b2 {
    background: /*[[color]]*/ !important;
}
/* text in searchbox when email is selected */
.r3 {
    color: /*[[color]]*/ !important;
}
/* background of banner when email is selected, &c. */
.bn, .bn .at, .jy {
    background: /*[[lightcolor]]*/ !important;
}
.b4 .at::before {
    box-shadow: inset -24px 0 12px -12px /*[[color]]*/ !important;
}
.gH {
    background-color: /*[[darkcolor]]*/;
}
}
