style.css:
@-moz-document domain("pcworld.pl") {
/* tło */
body , .footer.bs-docs-footer, figure, .panel, .pager li>a, .pager li>span, .right-side section, .right-side .ad, #lenovo .section-header .section-title {
	background-color: #161616;
}
.container, .navbar-default,
.articleBodyRanking .subtytul:before,
figcaption, .frame_src, .t-cell {
	background: #161616;
}
/* navbar */
.container-navbar-fixed > .container-navbar-inside > .navbar-default2 {
	box-shadow: 0 10px 10px rgba(0, 0, 0, 0.4);
}
.navbar-default2 {
	background: rgba(135, 0, 0, 0.85);
}
.navbar-nav > li > a.ss-icon {
	color: #ffffff;
}
/* logo */
.navbar-brand>img {
	filter: grayscale(100%) brightness(100);
	-webkit-filter: grayscale(100%) brightness(100);
}
/* text */
body,
.hot-topics, .hot-topics a,
.row-list-item .title, .file-program .row-list-item .title,
.row-box .title,
article, .breadcrumb, .breadcrumb a, .right-side a, figcaption,
nav#download-category > h3 > a, nav#download-category ul li a, .naviSekcja a, .moreList a, .deal-info .title, #lenovo .section-header .section-title {
	color: #ffffff;
}
a:hover, a:hover .title, .hot-topics a:hover, .right-side a:hover {
	color: #ff5555;
}
.row-list-item .description {
	color: #bbb;
}
.subtytul, .deal-info .price-value {
	color: #c53232;
}
/* art */
.tags > li a:hover {
	background: #600000;
}
#tresc .spis-tresci {
	background-color: rgba(255, 255, 255, 0.05);
	border: 1px solid rgba(255, 255, 255, 0.1);
}
.review-summary {
	background: rgba(255, 255, 255, 0.02);
}
.tags > li a {
	background: #363636;
}
.ranking-products .list-group > .list-group-item {
	background: rgba(255, 255, 255, 0.03);
}
.ranking-products .list-group-item.active, .ranking-products .list-group-item.active:focus, .ranking-products .list-group-item.active:hover {
	background-color: rgba(255, 255, 255, 0.03);
	border-color: rgba(255, 255, 255, 0.1);
}
.ranking-products .list-group-item .arrow-down {
	border-left: 1px solid rgba(255, 255, 255, 0.1);
}
.list-group-item {
	background-color: rgba(0, 0, 0, 0);
	border: 1px solid rgba(255, 255, 255, 0.1);
}
a.list-group-item:focus, a.list-group-item:hover, button.list-group-item:focus, button.list-group-item:hover {
	color: #888;
	background-color: rgba(245, 245, 245, 0.05);
}
a.list-group-item, button.list-group-item {
	color: #888;
}
.frame-idg-title {
	background: rgba(255, 255, 255, 0.05);
}
.ramka_slx {
	border: 1px solid rgba(255, 255, 255, 0.05);
}
/* download */
nav#download-category, .download-apps-info {
	background-color: rgba(255, 255, 255, 0.01);
}
nav#download-category > h3 {
	border-top: 5px solid rgba(0, 0, 0, 0);
}
nav#download-category ul li::before, .naviSekcja ul li::before {
	background-image: url( data:image/png,%89PNG%0D%0A%1A%0A%00%00%00%0DIHDR%00%00%00%0B%00%00%00%08%08%06%00%00%00%2F8%05%88%00%00%00%06bKGD%00%FF%00%FF%00%FF%A0%BD%A7%93%00%00%00%09pHYs%00%00%0B%13%00%00%0B%13%01%00%9A%9C%18%00%00%00%07tIME%07%E1%09%12%13%1E%2C%1E5%D1%F7%00%00%00QIDAT%18%D3c%F8%FF%FF%3F%03%12%EE%FF%FF%FF%7F%02%9A%18%1C%A3%0B%24%FC%87%80%04b%14%23kh%20F1%B2%86~dq%26%06%EC%E0%02%03%03%C3%07%0CQ%2C%A6%1A%FC%FF%FF%FF%FD%FF%FF%FF%E7%13r%06N%85%D8%14%EF%C7%A5%F0%FF%FF%FF%0C%00%21y%F31%EAd%25R%00%00%00%00IEND%AEB%60%82)
}
/* inne */
.frame-responsive img {
	border: 1px solid #000;
}
.form-control {
	color: #888;
	background-color: #161616;
	border: 1px solid rgba(255, 255, 255, 0.25);
}
.input-group-addon, #newsletters .form-control, #newsletters .form-control:focus, .navbar-default .navbar-form .form-control, .navbar-default .navbar-form .form-control:focus {
	border: 1px solid rgba(255, 255, 255, 0.25);
}
#newsletters .form-control, #newsletters .form-control:focus, .navbar-default .navbar-form .form-control, .navbar-default .navbar-form .form-control:focus {
	border-left: 0!important;
}
#cookie-info {
	background: #333;
}
.btn-default {
	background-color: #500000;
	border-color: #600000;
}
.btn-default:hover {
	background-color: #400000;
	border-color: #600000;
}
.pager li > a:focus, .pager li > a:hover, .pager .next > a, .pager .next > span, .pager .previous > a, .pager .previous > span {
	background-color: #500000;
}
.idg-offer-item:nth-child(even) {
	background: #222222;
}
.idg-offer-item:hover {
	background-color: #2d2d2d;
	color: #ccc;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=pcworldpl") {
/* DISQUS */
body, .tab-conversation.active>a, .comment__footer .author, .comment__header .author, .nav-tab>a, .dropdown-toggle, .discovery-post .top-comment p, .discovery-col-header h2 {
	color: #fff!important;
}
.nav-primary {
	border-bottom: 2px solid rgba(255, 255, 255, 0.08);
}
.disqus-footer__wrapper {
	border-top: 2px solid rgba(255, 255, 255, 0.08);
}
h5, h6, .comment__footer .vote-down, .comment__footer a, .comment__header .vote-down, .comment__header a {
	color: rgba(255,255,255,.5);
}
.comment__footer .vote-down:hover, .comment__footer a:hover, .comment__header .vote-down:hover, .comment__header a:hover {
	color: rgba(255,255,255,.85);
}
.discovery-post .top-comment .user {
	color: #288ce4;
}
.discovery-post .meta {
	color: #777;
}
.discovery-col-header h2 strong {
	color: #ff8888;
}
.disqus-footer__link {
	color: rgba(255,255,255,.5);
}
.disqus-footer__logo .disqus-footer__link {
	background-position: 0 -54.5px;
}
}
