	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}


	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}


	/*Removing text decorations*/
	*
	{
		text-decoration: none !important;
	}

	body, html, #page::before, .sidebar-offcanvas, #secondary
	{
		background-color: var(--second-background) !important;
		color: var(--dimer-text) !important;
	}
	#page, #primary, #primary .page-title
	{
		background-color: var(--main-background) !important;
	}
	a, a:visited
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	a:hover
	{
		opacity: 1;
	}

	p, li, h1, h2, h3, h4, span
	{
		color: var(--main-text) !important;
	}
	.date
	{
		color: var(--dimer-text) !important;
	}
	font
	{
		color: var(--main-color) !important;
		opacity: .9;
	}

	#header
	{
		background: var(--hover-background) !important;
		border-radius: 0 0 50px 50px !important;
	}

	.hentry, #primary .page-title
	{
		border-color: var(--hover-background) !important;
	}
	.hentry .entry-meta
	{
		color: var(--dimer-text) !important;
	}

	a.url.fn.n:hover span
	{
		color: var(--main-color) !important;
		opacity: 1;
	}

	/*Logo*/
	h1 img
	{
		display: block !important;
		-moz-box-sizing: border-box !important;
		box-sizing: border-box !important;
		background: url(https://raw.githubusercontent.com/RaitaroH/LinuxMint_Blog-Deepdark/master/Images/linux_mint_logo.png) no-repeat !important; /*image adapted from https://github.com/keeferrourke/la-capitaine-icon-theme*/
		width: 150px !important;
		height: 134px !important;
		padding-left: 150px !important;
		position: relative !important;
	}

	/*Search*/
	#search input, #searchform .field
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	#search input:hover, #search input:active, #search input:focus, #searchform .field:focus, #searchform .field:hover
	{
		border-color: var(--main-color) !important;
	}
	img[src="http://blog.linuxmint.com/wp-content/themes/say-it-10/images/search.png"]
	{
		display: block !important;
		-moz-box-sizing: border-box !important;
		box-sizing: border-box !important;
		background: url(https://raw.githubusercontent.com/RaitaroH/LinuxMint_Blog-Deepdark/master/Images/search.png) no-repeat !important;
		width: 14px !important;
		height: 14px !important;
		padding-left: 14px !important;
		position: relative !important;
	}


	/*First post*/
	.post_firstpost
	{
		background-image: none !important;
		border-color: var(--main-color) !important;
		border-style: dashed !important;
	}


	/*Image border*/
	.wp-caption
	{
		border-color: var(--hover-background) !important;
	}


	/*Sponsorships*/
	td
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	td img, table
	{
		border-color: var(--hover-background) !important;
	}


	/*Bubble*/
	.postmetadata2, .postmetadata
	{
		background: transparent !important;
		border: 5px solid var(--hover-background) !important;
		border-radius: 20px !important;
	}

	.single .post-navigation
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.single .post-title:hover, .single .post-navigation .meta-nav:hover
	{
		color: var(--main-color) !important;
	}


	/*Comments*/
	textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"]
	{
		color: var(--dimer-text) !important;
	}
	cite, #comments .comment .fn
	{
		color: var(--main-color) !important;
	}
	.commentmetadata a
	{
		opacity: .5;
	}
	.commentmetadata a:hover
	{
		opacity: 1;
	}
	#comments .comment, #comments .children, #comments, .comment #respond
	{
		border-color: var(--hover-background) !important;
	}

	#author, #email, #url, #comment, #submit
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
		box-shadow: none !important;
	}
	#author:hover, #email:hover, #url:hover, #comment:hover, #author:focus, #email:focus, #url:focus, #comment:focus, #author:active, #email:active, #url:active, #comment:active
	{
		border-color: var(--main-color) !important;
	}
	#submit:hover
	{
		color: var(--main-color) !important;
	}


	/*Main news*/
	#sidebar h2
	{
		background-image: none !important;
		border-color: var(--hover-background) !important;
		padding-left: 15px !important;
	}


	/*Code*/
	.crayon-syntax
	{
		border-color: var(--hover-background) !important;
	}
	.crayon-theme-monokai .crayon-striped-line
	{
		background: var(--hover-background) !important;
	}


	/*Footer*/
	#footer
	{
		background: var(--hover-background) !important;
		border-radius: 50px 50px 0px 0px !important;
	}
	.site-info
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}


	/*Pagination*/
	.pagination .dots, .pagination .page-numbers
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.pagination .current, .pagination .dots:hover, .pagination .page-numbers:hover
	{
		color: var(--main-color) !important;
	}
}
