style.css:
@-moz-document domain("www.cartoonson.com") {
.site-wrapper {
    background-color: #1e2124;
}

.page-content-body {
    background-color: #2f3136;
}

.page-wrapper {
    background: #2f3136;
}

hr {
    border-color: #4a4a4a;
}

#header {
    color: #1e2124;
    background-color: #1e2124;
}

.navbar {
    background: linear-gradient(to bottom, rgba(30, 33, 36, 1) 50%, rgba(30, 33, 36, 0.5) 100%);
}

.main-nav {
    background: linear-gradient(to top, rgba(30, 33, 36, 1) 50%, rgba(30, 33, 36, 0.5) 100%);
}

#_search_site {
    color: #484b51;
    background-color: #36393e;
    border: 1px solid #2f3136;
}

.play-episode-btn {
    background-color: #36393e;
}

.panel-title {
    color: #848494
}

.panel-title {
    background-color: #36393e;
    background-image: none;
}

.panel-default {
    border-color: #2f3136;
}

.btn-default {
    border: none;
}

.media-list {
    background-color: #1e2124;
}

.grid-items-list .media {
	background: none repeat scroll 0 0 #1e2124;
}

.image-overlay {
    background-color: #1e2124
}

#under_content {
    background-color: #3d3d3d;
}

.pagination>li>a, .pagination>li>a:focus {
    background-color: #1e2124;
    border: 1px solid #3d3d3d;
}
.dropdown-menu {
    background-color: #2f3136;
}

.dropdown-menu>li>a {
    color: #848494;
}
}

@-moz-document url-prefix("http://www.cartoonson.com/cartoons/watch/") {
.btn-default {
    border: none;
    background-color: #2f3136;
    text-shadow: 0 1px 0 #000
}
}
