@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* 
Swing Button for Multiple Paste and Go Button
https://userstyles.org/styles/148546/swing-button-for-multiple-paste-and-go-button
Multiple Paste and Go Button
https://addons.mozilla.org/addon/multiple-paste-and-go-button/
 */
#multiple-paste-and-go-button_wantora_github_io-browser-action {
    -moz-transform: scale(1.15) !important; 
    background-image: none !important;
    background-position: center !important;
    background-repeat: no-repeat !important;
}
/*
#multiple-paste-and-go-button_wantora_github_io-browser-action:hover {
    -moz-transform: rotate(15deg) scale(1.15) !important;
    -moz-transition-duration: 0.2s !important; 
    background: none !important;
}
*/
#multiple-paste-and-go-button_wantora_github_io-browser-action:active {
    -moz-transform: rotate(-15deg) scale(1.15) !important;
    background: none !important;
}
#multiple-paste-and-go-button_wantora_github_io-browser-action .toolbarbutton-badge-stack {
    list-style-image: url("chrome://browser/skin/tabbrowser/newtab.svg") !important;
    background: none !important;
    border: none !important;
}
