style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reactor.cc"), domain("joyreactor.cc"){
  div#header{
    width: 90%;
  }
  div#page{
    width: 90%;
  }
  div#content{
    width: 100%;
  }
  div#tagArticle{
    width: 100%;
  }
  div#sidebar{
    right: -310px;
    width: 320px;
    transition: 0.2s;
    position: absolute;
    background-color: white;
    padding-left: 10px;
    padding-right: 10px;
    z-index: 10;
    border-left: 2px solid lightgray;
    border-bottom: 2px solid lightgray;
  }
  div#sidebar:hover {
    right: 0;
    box-shadow: -6px 0px 20px -5px rgba(0, 0, 0, 0.47);
  }
  div#contentinner {
    width: 100%;
  }
  div#showCreatePost {
    width: 100%;
  }
  div#add_post_holder {
    width: 100%;
  }
  div[id^=postContainer]{
    box-shadow: 10px 0px 20px -10px rgba(0, 0, 0, 0.4);
  }
  div#navcontainer {
    background-size: 100%;
  }   
  div#searchBar {
    background-size: 100%;
  }  
  div#blogName {
   width: 85%;
  } 
  .article .ufoot {
    width: 100% !important;
  }
  .image {
	  text-align: center !important;
  }
}
