style.css:
@-moz-document regexp("^https?:\\/\\/answers\\.unrealengine\\.com\\/.*") {
/*===============================  global start  ===================================*/
body {
	background-color: #333;
	color: #ccc;
}

h1, h2, h3, h4, h5, h6 {
	color: #ccc;
}

a,
.span3 a {
	color: #09f;
	transition-duration: 0.5s;
}

a:hover {
	color: #f90;
	text-decoration: none;
	transition-duration: 0.2s;
}

.span9 > h2 {
	color: #f90;
	padding: 0 10px;
	background-color: #454545;
	border-radius: 4px;
}

input {
	margin: 0;
	background-color: #454545;
	border: none;
	color: #ccc;
	box-shadow: 0 0 0 1px #454545 inset;
}

input:focus {
	box-shadow: 0 0 0 1px #f90 inset;

}

select {
	background-color: #454545;
	border: none;
	color: #ccc;
}

label {
	color: #ccc;
}

li > p {
	margin-bottom: 0;
}


.container {
	min-width: 940px;
	width: calc(100% - 200px);
}

.span9 {
	min-width: 700px;
	width: calc(100% - 40px - 220px);
}

.span12 {
	width: calc(100% - 20px);
}

/*================================  global end  ====================================*/

/*===============================  Buttons start  ==================================*/

div.comment-tools a.add-comment-link {
	background-color: #666 !important;
	text-shadow: none;
	color: #ccc;
	border: none !important;
	box-shadow: none;
}

div.comment-tools a.add-comment-link:hover {
	color: #333 !important;
	background-color: #F90 !important;
}

.btn,
.btn-primary {
	background: #666 none;
	box-shadow: none;      
	border: none;  
	text-shadow: none;
}

.btn:hover,
.btn-primary:hover {
	background: #f90; 
}

.btn.active:hover {
	background-color: #e6e6e6;
}

#id_main_ask_btn {
	transition-duration: 0.5s;
}

.welcome-bar .btn-large {
	background-image: none;
	border: none;
	background-color: #808080;
	border-radius: 2px;
}

.welcome-bar .btn-large:hover {
	background-color: #F90;
}

.span3 a.btn-large {
	text-shadow: none;
}

/*================================  Buttons end  ===================================*/

/*===============================  editor start  =====================++============*/
#fmanswer .form-item {
	display: flex;
	flex-direction: column;  
}

.wmd-button-bar,
#wmd-button-bar {
	background-color: #333;
	width: auto;
	float: inherit;
}

#wmd-button-bar {
	border: 1px solid #454545;
}


.wmd-button-row {
	background-color: #333; 
	margin: 8px;
}

.wmd-button {
	background-color: #333; 
	filter: saturate(0) invert(100%);
	background-blend-mode: exclusion;        
}

.wmd-button:hover { 
	background-color: #333;
}

.wmd-spacer {
	background-color: #454545; 
}


form {
	margin: 0;
}

textarea {
	background-color: #454545;
	color: #ccc;
	border-radius: 0;
	border: none;
	box-shadow: 0 0 0 1px rgba(0,0,0,0) inset;
}

textarea:focus{
	box-shadow: 0 0 0 1px rgba(0,0,0,0) inset;
}

textarea:hover {
	box-shadow: 0 0 0 1px #f90 inset;
}

#fmanswer .resizable-textarea > span,    
#fmask .resizable-textarea > span {
	display: flex;
	flex-direction: column;
}


#fmanswer .resizable-textarea,
#fmask .resizable-textarea {
	background-color: #454545;
	border-radius: 0 0 4px 4px;
}

.comment-form-widgets-container .comment-form-textarea-container {
	width: auto;
	float: none;
	display: flex;
	flex-direction: column;
}

.comment-form-widgets-container .comment-form-textarea-container > br {
	display: none;
}

.comment-form-textarea-container textarea,
#fmanswer textarea,
#fmask textarea {       
	padding: 10px;
	margin: 0;
	width: auto !important;
	border-radius: 4px;
}

.comment-form-widgets-container textarea {
	padding: 10px;
	margin: 0;
	width: auto;
}

.wmd-preview {
	background-color: #454545;
	width: auto;
	border-radius: 4px;
	margin-top: 0px;
	padding: 10px 20px;
}

.preview-toggle {
	margin-top: 18px;
}

.preview-toggle #pre-collapse {
	background-color: #666;
	padding: 6px 10px;
	border-radius: 2px;
	color: #ccc;
	display: inline-block;
	transition-duration: 0.5s
}

.preview-toggle #pre-collapse:hover {
	background-color: #F90;
	color: #333;
	transition-duration: 0.1s
}


.form-actions {
	background-color: #454545;
	border-top: none;
	border-radius: 4px;
}

.comment-form-container {
	padding: 0;
	border-radius: 4px;
	overflow: hidden;
}

span.comment-chars-left {
	margin: 8px auto 0 3px;
	display: inline;
	width: auto;
}

.comment-form-buttons {
	width: auto;
	float: none;
	margin: -24px 3px 3px auto;
}

div.wmd-button-bar {
	float: none;
}


.comment-form-buttons-container {
	margin-top: 0px !important;
	display: inline-block;
}

.comment-form-buttons-container div {
	float: none !important;
	display: inline-block;
}

.comment-recipients-label {
	position: relative;
	top: 2px;
	left: 0;
}

.comment-form-widgets-container {
	background-color: #393939;
	border: 1px solid #393939;
}

.btn-primary[disabled] {
	background: none;
	box-shadow: 0 0 0 1px #666 inset;
}

div.dialog {
	background-color: #393939;
	box-shadow: 2px 3px 7px 0 rgba(0,0,0,0.5);
	border: 1px solid #454545;
}

/*================================  editor end  ==========================+=========*/


/*================================  Popup start  ===================================*/
.popover-inner {
	background-color: #454545;
	padding: 1px;
}

.popover-title {
	background-color: #454545;
	border-bottom: none;
	color: #ccc;
}

.popover-content {
	background-color: #393939;
}

.popover-content a {
	color: #f90;
	font-size: 18px;
}

.popover.top .arrow {
	border-top-color: #808080;
}
/*================================  Popup end  =====================================*/


/*===========================  search results start  ===============================*/

#search-options-popover .popover-title {
	border-radius: 0;
}

.popover-content .popover-title {
	margin: 10px -15px;
}

.similar-questions-container .popover-title {
	margin: 10px -15px;
}

.similar-questions-results .search-options-result {
	border-color: #454545;
	padding: 5px 0;
	margin: 0;
}    

.popover-content .search-options-result a {
	font-size: 14px;
	color: #09f;
}

.popover-content .search-options-result a:hover {
	color: #f90;
}

.similar-questions-results .search-options-result-type.question {
	background-color: #393939;
	border-color: #505050;
	color: #ccc;
	padding: 2px 4px;
	border-radius: 7px;
}

.tabBar .headQuestions {
	position: relative;
	padding: 0 10px;
	background-color: #454545;
}





#search-nav-tabs {
	border-radius: 4px 4px 0 0;
}


.headQuestions > ul{
	position: absolute;
	right: 5px;
	top: -35px;
}

#advanced-search-clean {
	color: #333;
	margin-left: 62px;
}



div.short-summary.search-hit {
	margin: 5px 0 0 0;
}

div.short-summary.search-hit .top-wrapper {
	display: block;
	padding: 0 10px 20px 55px;
	width: auto;
	min-height: 50px;
}

div.short-summary.search-hit .top-wrapper > .counts {
	position: absolute;
	left: 0;
}

div.short-summary.search-hit .top-wrapper > h2 {
	margin: 0 150px 20px 0 ;
	padding-left: 0;
}

div.short-summary.search-hit .top-wrapper > .question-section {
	position: absolute;
	right: 0;
	top: 0;
}

div.short-summary.search-hit .top-wrapper > div:last-child {
	/*position: absolute;
	right: 10px;
	bottom: 20px;*/
	text-align: right;
	margin: 10px 0 5px 0;
}

div.short-summary.search-hit .top-wrapper > div:last-child > small {
	color: #666;
}

div.short-summary.search-hit .top-wrapper div:last-child > small > a {
	color: #c3aa88;
}

div.short-summary.search-hit .tags {
	padding-bottom: 3px;
	margin-left: 5px;
	float: none;
	bottom: 0;
}


.short-summary.search-hit b {
	color: #ccc;
	background-color: transparent;
	box-shadow: 0 0 0 1px #f90 inset;
	margin: 0 2px;
	padding: 0 2px;
	border-radius: 4px;
}



/*===========================  search results end ==================================*/    


/*===================+======  Post a Question start  ===============================*/

.ask-body {
	padding-right: 0;
}

#askform {
	color: #808080;
}

#askform #title {
	font-size: 20px;
	height: 28px;
	font-weight: normal;
	box-sizing: border-box;
}

#askform #previewer {
	color: #ccc;
}


label span {
	color: #808080;
}

#topics_tagsinput {
	background-color: #454545;
	border: none;
	border-radius: 4px;
	box-sizing: border-box;
	width: 100% !important;
}

#topics_tag {
	color: #ccc;
}

.darkred { /* i know, it's not red at all */
	color: #9f0;
}

div.tagsinput span.tag {
	padding: 0px 4px;
	line-height: 15px;
	margin-top: 5px;
}

#submit-question {
	margin-top: 0;
}

.fieldError {
	color: #f30;
}


.ui-widget-content {
	background-image: none;
	background-color: #393939;
	color: #808080;
}

.ui-widget-content a {
	color: #ccc;
}

.ui-state-hover,
.ui-widget-content .ui-state-hover {
	background-image: none;
	border: none;
	background-color: #393939;
}
.ui-menu .ui-menu-item a.ui-state-hover {
	margin: 0;
	color: #f90;
}

.ui-autocomplete {
	border-radius: 0 0 4px 4px;
	border-top: none;
	border-color: #666;
	box-shadow: 2px 3px 7px 0 rgba(0,0,0,0.5);
}



/*==========================  Post a Question end  =================================*/


/*==============================  user info start  =================================*/

/*user stats start*/
.user-stats-table img {
	display: none;
}
.user-stats-table td:first-child .vote-count:before {
	font-size: large;
	content: "‚Æù";
	color: #9F3;
	margin-right: 5px;
}    
.user-stats-table td:last-child .vote-count:before {
	font-size: large;
	content: "‚Æü";
	color: #f30;
	margin-right: 5px;
}    
/*user stats end*/

/*=============================  user info end  ====================================*/


/*================================  footer   ===================================*/
.pagination a {
	border-color: #666;
	background-color: #454545;
}

.pagination .active a {
	background-color: #454545;
	color: #f90;
}

.pagination a:hover {
	background-color: #666;
	color: #f90;
}

.pagination .active a:hover {
	background-color: #454545;
}

.pagination .disabled a {
	border-color: #666;
}

.pagination li span {
	border-color: #666;
	border-radius: 3px 0 0 3px;
}

/*================================  footer end  ===================================*/

.questions-related a {
	display: block;
}    

/* .tags a {
background-color: #808080;
color: #333;
border: none;
border-radius: 7px;
}*/

div.tagsinput span.tag,
span.tag,
.tags a {
	background-color: #999;
	color: #333;
	border: none;
	border-radius: 7px;
}

.userAndKarmainfo + .tags a {
	background-color: #393939;
	color: #808080;
	border: none;
	border-radius: 7px;
	box-shadow: 0 0 0 1px #505050 inset;
}

div.tagsinput span.tag:hover,
span.tag:hover,
.tags a:hover {
	background-color: #F90;
	color: #333;
}

.userAndKarmainfo + .tags a:hover {
	box-shadow: 0 0 0 1px #f90 inset;
}

#recent-tags {
	line-height: 22px;
}

#recent-tags > a {
	display: inline-block;
	line-height: 12px;
}

#question-tags a {
	margin: 0 1px;
}

#retag-question {
	background-image: none;
	padding: 1px 8px 1px 5px;
}

#retag-question:before {
	content: "‚úê";
}

div.tagsinput {
	background-color: #333;
	border: none;
	border-radius: 4px;
	height: auto !important;
}

#retag-question-box_tag {
	color: #ccc !important;
}

/*============================  top toolbar start  ========================+========*/


.breadcrumb {
	background-image: none;
	background-color: #393939;
	box-shadow: none;
	border-color: #666;
}

.breadcrumb li {
	text-shadow: none;
}


.navigation-tabs-content > h2 {
	display: none;
}

.span3 h3 {
	color: #F90;
	font-variant: small-caps;
	font-weight: lighter;
}

.dropdown-menu {
	background-color: #454545;
	border: none;
}

.dropdown-menu a {
	color: #ccc;
}

.score,
.bronze,
.silver,
.gold,
.badge1 {
	color: #ccc;
}

.nav .score {
	color: #F90;
}    
/*=============================  top toolbar end  ========================+=========*/



/*============================  Main tabs start  ===================================*/
.span9 > .tabs {
	margin-top: 15px !important;
}

.nav {
	margin-bottom: 0;       
}

.nav-tabs {
	border: none;
	padding-top: 0;
	background-color: #454545;
	border-radius: 4px;
}

.nav-tabs > .active > a,
.nav-tabs > .active > a:hover {
	color: #ccc;
}

.nav-tabs > li {
	margin-bottom: 0;
}

.nav-tabs > li a {
	color: #ccc;
}

#main-content-container .nav-tabs > .active > a:hover,
.nav-tabs > .active > a {
	box-shadow: 0 3px 0 0 #f90 inset;
	background-color: #454545;
	border-radius: 0 0 4px 4px;
	color: #ccc;
}

#main-content-container .nav-tabs > li > a {
	margin: 0;               
	border-radius: 0;
	border: none;
}

#main-content-container .nav-tabs > li > a:hover {
	box-shadow: none;
	background-color: #393939;
	border: none;
	color: #f90;
}

#main-content-container .nav-tabs > li:first-child > a:hover {
	border-radius: 4px 0 0 4px;
}

/*===============+==============  Main tabs end  ===================================*/



.message {
	background-color: #454545;
	border-radius: 4px;
	border-color: #666;
}


/* side boxes start */   
.boxC,
.well {
	background-color: #454545;
	border: none;
	padding: 15px;
	box-shadow: none;
}

.span4 {
	width: auto;
	float: none;
}

.well.callout {
	width: auto !important;
	margin-top: 15px;
}

.questions-count {
	color: #f90;
	padding: 0;
	margin: 8px 0;
}

.questions-count span{
	color: #ccc !important;
	float: right;
	width: 80px;
}



/* Top karma start */
#main-content-container .span3 > div:nth-child(4) > div {
	background-color: #454545 !important;
	border-radius: 2px;
}

#main-content-container .span3 > div:nth-child(4) > div {
	padding: 10px 19px 19px 19px;
}

#main-content-container .span3 > div:nth-child(4) > div > h3 > span {
	margin-left: 0 !important;
}

#main-content-container .span3 > div:nth-child(4) > div > table {
	border-style: hidden;
}

#main-content-container .span3 > div:nth-child(4) > div th {
	background-color: #454545;
	border-color: #393939;
	border-right-color: #454545;
}

#main-content-container .span3 > div:nth-child(4) > div th:nth-child(1) {
	width: 70% !important;
}

#main-content-container .span3 > div:nth-child(4) > div th:nth-child(2) {
	width: 30% !important;
}

#main-content-container .span3 > div:nth-child(4) > div td {
	border-color: #393939;
	border-right-color: #454545;
	padding: 2px 0;
}

#main-content-container .span3 > div:nth-child(4) > div tr:nth-child(-n+4) > td {
	color: #9F0;
}    
/* Top karma end */   
/*=============================  side boxes end  ===================================*/


/*===========================  user info page start  ===============================*/

.action_container {
	border: none;
	margin: 5px 0;
}

.question-title-link a {
	font-weight: normal;
}

.answer-title-link a {
	color: #9f0;
}

.comment-title-link a {
	color: #ccc;
}

.comment-title-link a:hover,
.answer-title-link a:hover {
	color: #f90;
}

/*============================  user info page end  ================================*/


.tabBar {
	background-color: #454545;
	border: none;
	border-radius: 4px;
}

.tabsA {
	background-color: transparent;
}

.tabsA a {
	background-color: #666;
	border: none;
	/*border-top: 2px solid #666;*/
	/*border-radius: 4px;*/
	color: #ccc;
	margin: 0;
}

.tabsA a:hover,
.tabsA a.on:hover {
	color: #F90;
	background-color: #666;
}

.tabsA a.on {
	background-color: #666;
	color: #ccc;
	border-top: 2px solid #f90;
	margin-top: -2px;
}

a.medal {
	background-color: #333;
	border-color: #666;
	color: #999;
	border-radius: 13px;
}

a.medal:hover {
	color: #F90;
	border-color: #F90;
}



#opened-before { /*warning! I have no clue what supposed to be in this container*/
	display: none;
}

#main-content-container .span9 > div:first-child {
	height: auto !important;
}


.span9,
#listA,
.navigation-tabs-content,
.homepage-question-block {
	display: flex;
	flex-wrap: wrap;
}

.span9 > div:not(.pagination):not(.pagesize),
#listA > div:not(.pagination):not(.pagesize),
.navigation-tabs-content > div:not(.pagination):not(.pagesize),
.homepage-question-block > div:not(.pagination):not(.pagesize) {
	flex-basis: 100%;
}
.span9 > .pagesize,
#listA > .pagesize,
.homepage-question-block > .pagesize, 
.navigation-tabs-content > .pagesize {
	margin-left: auto;
}

.homepage-question-block > .tabs {
	background-color: #333;
	margin-top: 0 !important;
}

.sidebarSpaces ul {
	margin: 0 0 0 10px;
	font-variant: small-caps;
}

.short-summary {
	background-color: #393939;
	border-top: none;
	margin-top: 5px;
	float: none;
	overflow: visible;
	border-radius: 4px;
	padding: 0;
}

.short-summary:nth-child(odd) {
	background-color: #363636;
}	

.short-summary:empty {
	display: none;
}

.short-summary + div:not(.pagination):not(.short-summary):not(.row) {
	border-radius: 4px;
	margin: -7px 0 0 5px;
	z-index: 100;
	width: auto;
	display: flex;
	flex-direction: row-reverse;
}

.short-summary + div > .userAndKarmainfo {
	float: none !important;
	margin-left: auto;
	margin-right: 10px;
	margin-top: -33px;
}

.short-summary + div > .userAndKarmainfo > a {
	color: #c49944;
}






.answer-votes {
	width: 15px;
	border-radius: 13px;
	background-color: #666;
	color: #333;
}

.answered-accepted, .answer-votes.answered-accepted {
	background-color: #666;
	color: #9f3;
}

.user-stats-table .answer-link a {
	vertical-align: text-top;
	line-height: 20px;
}


