.short-summary .question-summary-wrapper {
	display: flex;
	flex-direction: column;
}*/
    
.short-summary .black {
	color: #808080;
}

.short-summary .question-section {
	color: #808080;
	margin: 5px 10px 0 5px;
	width: 150px;
	flex-shrink: 0;
	/*display: flex;
	flex-wrap: wrap;
	align-content: flex-start;*/
	font-size: 0;

}

.short-summary .userinfo,
.question-section > span,
.product-version span {
	color: #ccc !important;
}   

.question-section > a {
	display: inline-block;
	float: right;
	font-size: 11px;
	/*flex-basis: 50%;
	flex-shrink: 0;
	flex-grow: 1;
	text-align: right;*/
}

.question-section > span {
	display: inline-block;
	position: absolute;
	font-size: 11px;
	left: 70px;
	bottom: 25px;
}

.question-section > span:before {
	content: "ver.";
	color: #666;

}



.short-summary .userinfo {
	margin: 0 10px 0 auto;
	/*width: 200px;*/
	/*align-self: flex-end;*/
	float: none;
	position: absolute;
	bottom: 4px;
	right:0;
	display: flex;
	flex-wrap: wrap;
}

.short-summary .userinfo > * {
	flex-basis: 100%;
	text-align: right;      
}

.short-summary .userinfo > span:first-child {
	padding-bottom: 18px;
}

.short-summary .relativetime {
	display: inline-block;
	margin-left: auto;
	color: #ccc;
}

.short-summary h2 {
	flex-grow: 1;
	padding: 5px;
	margin-bottom: 37px;
	font-size: 1.2em;
	line-height: 130%;
}

.short-summary h2 a {
	color: #ddd;
	font-weight: normal;
}

.short-summary h2 a:hover{
	color: #f90;
}

