style.css:
@-moz-document regexp("https?://(www.)?youtube.com/(?!dashboard|my_videos|edit|analytics|features|audiolibrary/music|comments)(.*)") {
:root {
    --purple: #A68ED2 !important;
    --greybg: hsl(0, 0%, 11%) !important;
    --midgrey: hsla(0, 0%, 12%, 1) !important;
    --lightgrey: hsl(0, 0%, 20%) !important;
    --bordercolor: hsla(0, 0%, 30%, 1) !important;
    --textcolor: hsla(0, 0%, 80%, 1) !important;
    --textcolor2: hsla(0, 0%, 50%, 1) !important;
    --textcolor3: hsla(0, 0%, 70%, 1) !important;
}

/**** New Layout ****/
html:not([style-scope]):not(.style-scope):not([color-exp]) *, html[color-exp]:not([style-scope]):not(.style-scope) {
    --yt-primary-color: var(--purple);
    --yt-primary-text-color: var(--textcolor);
    --ytd-comment-text-color: var(--purple);
    --yt-hovered-text-color: var(--purple);
    --yt-secondary-text-color: var(--textcolor);
    --yt-disabled-text-color: hsla(0, 0%, 6.7%, 0.3);
    --yt-placeholder-text-color: var(--textcolor);
    --yt-border-color: var(--bordercolor);
    --yt-searchbox-border-color: var(--textcolor);
    --yt-searchbox-legacy-border-color: var(--textcolor3);
    --yt-searchbox-legacy-border-shadow-color: none;
    --yt-searchbox-legacy-button-color: var(--greybg);
    --yt-spec-text-primary:var(--textcolor);
    --yt-spec-brand-button-background:var(--lightgrey);
    --yt-guide-entry-text-color:var(--textcolor2);
    --yt-searchbox-legacy-button-border-color: var(--textcolor);
    --yt-searchbox-legacy-button-focus-color: var(--textcolor);
    --yt-searchbox-legacy-button-hover-color: var(--textcolor3);
    --yt-searchbox-legacy-button-hover-border-color: var(--textcolor);
    --ytd-searchbox-legacy-button-icon-color: var(--textcolor);
    --yt-searchbox-legacy-button-icon-color: var(--textcolor);
    --ytd-searchbox-legacy-button-color: var(--lightgrey)!important;
    --ytd-collection-badge-color: var(--textcolor2);
    --yt-searchbox-background: var(--greybg)!important;
    --yt-commentbox-border-inactive: hsl(0, 0%, 93.3%);
    --yt-commentbox-border-active: hsla(0, 0%, 6.7%, .6);
    --yt-primary-disabled-button-text-color: hsl(0, 0%, 100%);
    --yt-disabled-paper-button-color: var(--yt-disabled-text-color);
    --yt-brand-paper-button-color: var(--lightgrey)!important;
    --yt-paper-radio-button-checked-color: hsl(0, 0%, 53.3%);
    --yt-paper-button-ink-color: hsl(0, 0%, 53.3%);
    --yt-icon-color: var(--textcolor);
    --yt-icon-hover-color: var(--purple);
    --yt-icon-disabled-color: hsla(0, 0%, 6.7%, .6);
    --yt-icon-active-color: hsl(0, 0%, 6.7%);
    --yt-expand-color: var(--textcolor2);
    --yt-metadata-color: var(--textcolor);
    --yt-toggle-color: var(--purple);
    --yt-playlist-background-header: var(--lightgrey);
    --yt-playlist-background-item: var(--lightgrey);
    --yt-playlist-title-text: hsl(0, 0%, 6.7%);
    --yt-playlist-message-text: var(--textcolor2);
    --yt-subscribe-button-text-color: var(--textcolor);
    --yt-brand-color: hsl(3, 81.8%, 49.6%);
    --yt-button-text-color: var(--purple);
    --yt-button-payment-text-color: hsl(0, 0%, 100%);
    --yt-video-publish-date-color: var(--textcolor2);
    --yt-vat-notice-text: hsla(0, 0%, 6.7%, .6);
    --yt-copyright-text: var(--textcolor);
    --yt-guide-entry-hover-background-color: var(--lightgrey);
    --yt-spec-call-to-action: var(--textcolor2);
    --yt-survey-button-color: hsl(206.1, 79.3%, 52.7%);
    --yt-thumbnail-placeholder-color: hsl(0, 0%, 89%);
    --yt-transcript-cue-hover-background-color: hsl(0, 0%, 93.3%);
    --yt-transcript-toolbar-background-color: hsl(0, 0%, 93.3%);
    --yt-transcript-toolbar-text: hsl(0, 0%, 6.7%);
    --yt-featured-channel-title-text-color: var(--textcolor);
    --yt-formatted-string-deemphasize-color: hsl(0, 0%, 53.3%);
    --yt-badge-background: var(--lightgrey);
    --yt-badge-disabled-color: hsla(0, 0%, 53.3%, .4);
    --yt-collection-badge-color: hsla(0, 0%, 6.7%, .8);
    --yt-owner-badge-color: var(--purple);
    --yt-simple-badge-color: var(--purple);
    --yt-ad-badge-text-color: var(--greybg);
    --yt-simple-menu-header-background: var(--lightgrey);
    --yt-item-section-header-color: hsla(0, 0%, 6.7%, .6);
    --yt-menu-hover-backgound-color: var(--lightgrey);
    --yt-menu-focus-background-color: hsla(0, 0%, 6.7%, .2);
    --yt-app-background: var(--greybg)!important;
    --yt-main-app-background: var(--greybg);
    --yt-main-app-background-tmp: var(--greybg);
    --yt-guide-background: var(--greybg);
    --yt-dialog-background: var(--greybg);
    --yt-channel-header-background: var(--midgrey);
    --yt-playlist-sidebar-background: hsl(0, 0%, 98%);
    --yt-transcript-background: hsl(0, 0%, 100%);
    --yt-watch-card-secondary-text-color: hsl(0, 0%, 53.3%);
    --yt-watch-card-album-header-background: hsl(0, 0%, 100%);
    --yt-comment-text-color: var(--textcolor3);
    --yt-comment-metadata-text-color: hsl(0, 0%, 53.3%);
    --yt-moderation-panel-background: hsla(0, 0%, 93.3%, .6);
    --yt-moderation-panel-hover: hsla(0, 0%, 93.3%, .8);
    --yt-moderation-panel-comment-text: hsl(0, 0%, 6.7%);
    --yt-moderation-panel-comment-metadata-text: hsla(0, 0%, 6.7%, .6);
    --yt-moderation-icon-color: hsla(0, 0%, 6.7%, .6);
    --yt-moderation-icon-hover-color: hsl(0, 0%, 6.7%);
    --yt-moderation-panel-likely-spam-divider: hsla(0, 0%, 53.3%, .1);
    --yt-backstage-metadata-text-color: hsl(0, 0%, 53.3%);
    --yt-backstage-video-link-background-color: hsla(0, 0%, 93.3%, .4);
    --yt-backstage-image-alert-color: hsla(0, 0%, 6.7%, .6);
    --yt-backstage-cancel-background-color: hsl(0, 0%, 100%);
    --yt-backstage-cancel-color: hsla(0, 0%, 6.7%, .4);
    --yt-shopping-product-info: hsla(0, 0%, 6.7%, .8);
    --yt-offer-background-color: hsla(0, 0%, 93.3%, .4);
    --ytd-section-list-renderer: var(--purple)!important;
    --yt-searchbox-text-color: var(--textcolor)!important;
    --yt-spec-10-percent-layer: #505053d6!important;
    /** --- **/
    --yt-swatch-important-text: var(--textcolor);
    --yt-swatch-logo-override: var(--textcolor);
    --yt-swatch-icon-color: var(--purple);
    --yt-swatch-primary: var(--lightgrey)!important;
    --yt-swatch-text: var(--textcolor)!important;
    /** -- **/
    --yt-button-color: var(--textcolor);
    --yt-endpoint-hover-color: var(--purple);
    --yt-endpoint-color: var(--textcolor);
    --yt-channel-owner: var(--purple);
    /**
 --yt-swatch-primary: rgb(35,35,35);
 --yt-swatch-primary-darker: rgb(32,32,32);
--yt-swatch-text: rgb(255,255,255);
--yt-swatch-important-text: rgb(255,255,255);
--yt-swatch-input-text: rgba(255,255,255,1);
--yt-swatch-textbox-bg: rgba(19,19,19,1);
--yt-swatch-logo-override: rgb(255,255,255);
--yt-swatch-icon-color: rgba(136,136,136,1);
  **/
}
.badge-style-type-live-now.ytd-badge-supported-renderer {
    color: var(--purple);
    border-color: var(--purple);
}
a.yt-simple-endpoint.yt-formatted-string {
    color: var(--textcolor2);
}
a.yt-simple-endpoint:hover.yt-formatted-string:hover {
    text-decoration: underline;
}
#video-title:hover {
    text-decoration: underline;
    color: var(--textcolor)!important;
}
#video-title.ytd-grid-playlist-renderer {
    text-decoration: none;
    color: var(--textcolor)!important;
}
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer {
    color: var(--textcolor);
}
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover {
    color: var(--purple);
}
ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer {
    background-color: var(--lightgrey);
    color: var(--purple);
    padding: 4px 8px;
    margin: 0;
}
paper-button.ytd-subscribe-button-renderer[subscribed] {
    background-color: var(--lightgrey);
    color: var(--purple);
}
.deemphasize.yt-formatted-string {
    color: var(--yt-formatted-string-deemphasize-color);
}
paper-menu {
    background: var(--midgrey);
    color: var(--paper-menu-color, var(--primary-text-color));
}
#like-bar.ytd-sentiment-bar-renderer {
