style.css:
@-moz-document domain("8ch.net"), domain("8ch.pl"), domain("vichan.net"), domain("lainchan.org"), domain("8kun.top") {
/*-----Board Toolbar---*/

/* Main Bar */
div.boardlist {
    background-color: #0D1021 !important;
    color: #0D1021;
}

/* Text and Icons */
div.boardlist a {
    color: #14ef9b;
}

/*-----Posts-----*/

/* Children */
div.post.reply {
    background-color: #1e0b38;
    border: 1px solid #1e0b38;
    margin-bottom: 2px;
    margin-left: 16px;
    margin-top: 2px;
}

/* OP */
body {
    background: #0D1021 none;
    color: #C5C8C6;
}

/*----Text Colors----*/

/* Chan name */
h1, div.subtitle {
    color: #ff6c7d !important;
}

/* Making a post */
form table tr th {
    background: #0c1021;
    border: 1px solid #0c1021;
    color: #FFF;
}

/* Name */
p.intro span.name {
    color: #41cdff;
}

/* Board Volunteer Name */
.intro span.capcode,
p.intro a.capcode,
p.intro a.nametag {
    color: red;
    margin-left: 0;
}

/* Main Text */
body {
    color: #14ef9b;
}

/* Subject */
p.intro span.subject {
    color: #ff6c7d;
}

/* [Reply], [Show All Posts], and other various links */
a:link,
a:visited,
p.intro a.email span.name {
    color: #41cdff;
}

/* Greentext Meme-arrows Quoting */
.quote {
    color: #ff6c7d;
}

/* Child links */
div.post.reply div.body a {
    color: #41cdff;
}

/* Click to Expand Info */
span.omitted {
    color: #ff6c7d;
}

div.banner, div.banner a {
    color: #14ef9b;
}


/* Posting Mode Question */
div.banner {
    background-color: #0D1021;
}


/*---etc---*/

/* Border */
hr {
    border: none;
    border-top: 1px solid #ffffff00;
    height: 0;
    clear: left;
    display: none;
}

/* Pages */
div.pages {
    background: #0D1021;
    border-color: #0D1021;
}
div.pages {
    color: #41cdff;
}


/*----Board Index---*/

/* About 8chan Header */
.box {
    background: #0D1021;
    border-color: #0D1021;
    color: #14ef9b;
}

/* The Word "modes" */
#boardlist span {
    color: #ff6c7d !important;
}

/* Boards by current popularity */
.box-title {
    background: #0D1021;
    color: #ff6c7d;
}

li.col-12:nth-child(2n+1) {
    background: #0D1021 !important;
}
li.col-12 {
    background-color: #0D1021;
}


/* Boards and their Active ISPs/Total posts */
table thead th {
    background: #0D1021;
    border-color: #0D1021;
    color: #ff6c7d;
}

table tbody tr:nth-of-type(2n) {
    background-color: #0D1021;
}


/* "Displaying results" */
tbody.board-list-omitted td {
    background: #0D1021;
    border-color: #0D1021;
}
}
