style.css:
@-moz-document domain("habitica.com")
{
  .card.col-10 div.card-body 
  {
    padding: 2px !important;
  }

  .card.col-10 div.card
  {
    margin-bottom: 10px !important;
  }

  .card.col-10 div.text
  {
    max-height: 150px;
    overflow: auto;
  }

  .card.col-10 div.card-body h3 
  {
    height: 25px !important;
  }

  .card.col-10 div.card-body hr 
  {
    margin: 5px !important;
  }

  .card.col-10 div.card-body .time 
  {
    margin-top: -20px !important;
    position: absolute;
    right: 15px;
  }

  div.col-2 div.avatar 
  {
    margin-top: 0px !important;
    padding-top: 0px !important;
    max-height: 90px !important;
  }

  div.col-2 
  {
    padding: 0px !important;
    max-width: 15% !important;
    max-height: 90px !important;
  }
}
