style.css:
@-moz-document url("https://www.eldarya.pl/pet"), url("https://www.eldarya.fr/pet"), url("https://www.eldarya.com/pet"), url("https://www.eldarya.ru/pet"), url("https://www.eldarya.com.br/pet"), url("https://www.eldarya.de/pet"), url("https://www.eldarya.es/pet"), url("https://www.eldarya.hu/pet"), url("https://www.eldarya.it/pet") {
/*WYGLĄD MAPKI*/
/*przesunięcie okienka mapki*/
  #treasure-hunt-interface
  {
    top: 55px;
    left: 0px;
  }
/*usunięcie przycięcia mapki*/
  #map-container
  {
    -webkit-mask-image: none;
  }
/*usunięcie przycięcia cienia mapki przy odbieraniu poszukiwań i w ich trakcie*/
  #treasure-hunt-result-overlay::before
  {
    -webkit-mask-image: none;
    height: 100%;
    top: -1px;
  }

  #pending-map-location-data-outer.active
  {
    -webkit-mask-image: none;
    height: 92%;
    top: 39px;
  }

  #capture-interface-outer.active
  {
    -webkit-mask-image: none;
    height: 92.5%;
    top: 39px;
  }
/*rozciągnięcie tytułów lokacji*/
  #treasure-hunt-result-overlay #result-content.active #result-header
  {
    left: -10px;
  }

  #map-container #map-location-preview-inner h5
  {
    width: 270px;
  }

  #pending-map-location-data #pending-location-header h4
  {
    width: 270px;
  }

  #treasure-hunt-result-overlay #result-content #map-result-name h5
  {
    width: 270px;
  }
/*lekkie rozciągnięcie przy itemach znalezionych na poszukiwaniach*/
  #treasure-hunt-result-overlay #result-content #result-list
  {
    width: 100%;
    max-height: 155px;
    overflow: auto;
    margin-top: 20px;
    margin-bottom: 20px;
  }
/*nie ma suwaka przy znalezionych łupach*/
  #treasure-hunt-result-overlay #result-content .mCSB_scrollTools .mCSB_draggerContainer
  {
    display: none;
  }
/*usuwa podświetlenie przy itemach znalezionych na poszukiwaniach i dodaje tło*/
  #treasure-hunt-result-overlay #result-content #result-list .th-result .th-result-img
  {
    filter: none;
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 5px;
  }
/*usuwa animację przy itemach znalezionych na poszukiwaniach*/
  #treasure-hunt-result-overlay #result-content-outer #result-content.found-objects #result-glow
  {
    display: none;
  }
/*zwężenie pola z opisem itemu*/
  #tooltip-box
  {
    max-width: 300px;
    white-space: normal;
  }
/*usuwa animację przy chowańcu znalezionym na poszukiwaniach*/
  #capture-interface #capture-pet-image #capture-glow
  {
    display: none;
  }
/*zmienia rozmiar przycisku "mój chowaniec"*/
  .pet-interface-container.treasure-hunt-interface-open #right-container #close-treasure-hunt-interface
  {
    width: 38%;
  }
/*zmienia położenie ikonki z ilością energii chowańca*/
  #treasure-hunt-interface .pet-resource-container
  {
    top: 1px;
    left: -20px;
  }





/*WYGLĄD OKIENKA LOKACJI*/
/*kształt i tło okienka*/
  .pet-interface-container #treasure-hunt-interface .lens
  {
    border-radius: 10px;
    border: 2px solid #fff;
    background: #34386f;
  }
/*brak obrazka w tle okienka lokacji*/
  #map-location-tooltips .map-location-tooltip .tooltip-bg
  {
    background: none;
  }
/*nazwa lokacji*/
  #map-location-tooltips .map-location-tooltip .map-location-name
  {
    z-index: 15;
    left: 8px;
    top: 40px;
    width: 205px;
    color: #fff;
    text-shadow: none;
  }
/*info o energii i czasie poszukiwań*/
  #map-location-tooltips .map-location-tooltip .energy-required, #map-location-tooltips .map-location-tooltip .time-to-explore
  {
    left: -75px;
    top: 30px;
  }

  #map-location-tooltips .map-location-tooltip .energy-required span, #map-location-tooltips .map-location-tooltip .time-to-explore span
  {
    left: -75px;
    top: 33px;
    color: #fff;
  }

  #map-location-tooltips .map-location-tooltip .map-location-infos
  {
    box-shadow: none;
    z-index: 20;
    background: rgba(255, 255, 255, 0);
  }
/*kółko z ilością itemów*/
#map-location-tooltips .map-location-tooltip .map-location-history
{
    position: absolute;
    left: -14px;
    top: 12px;
}









