style.css:
@-moz-document domain(mastodon.social)
{
*, p, a {
        font-family: "Open Sans";
}

textarea {
    background: #191b22;
    background: #282c37!important;
    color: white!important;
    border-radius: 0!important;
}
.compose-form__uploads-wrapper {
    background: #282c37;
}

.compose-form__buttons {
    background: none;
        box-shadow: none;
}

.emoji-picker__dropdown, .emoji-picker-dropdown {
    display: none;
}

.button {
        border-radius: 0;
    background: #707b97;
}
 
@media (min-width: 1000px)
  {
    .columns-area>div {
            width: 25%;
    max-width: 25%;
  }

  .columns-area>div>.column,
        .columns-area>div>.mastodon-column-container>.column
  {
    width: 100%;
  }
}

.compose-form .compose-form__buttons-wrapper {
    background:none;
}
.character-counter {
    color: #707b97!important;
}
}
