style.css:
@-moz-document domain("fi.nobelium.xyz") {
.body img {
display: none;
}
.navbar-collapse {
		font-family: monospace !important;
		font-size: 14px !important;
		line-height: 1.4 !important;
}

.navbar-brand img {
	content: url("https://i.imgur.com/5oUFLfl.png");
    width: 140px;
    height: 50px;
    vertical-align: middle;
}

.img-fluid.mt-4.mb-1 {
	content: url("https://i.imgur.com/5oUFLfl.png");
    width: 140px;
    height: 50px;
    vertical-align: middle;
}
.n-btn-games-small {
    font-family: Comic Sans MS;
    color: rgb(0,0,0)!important;
    background-color: rgb(255,255,255)!important;
    border-color: rgb(0,0,0)!important;
}

.n-games-btn {
    font-family: Comic Sans MS;
    color: rgb(0,0,0)!important;
    background-color: rgb(255,255,255)!important;
    border-color: rgb(0,0,0)!important;
}

.custom-control-indicator {
    font-family: Comic Sans MS;
    color: rgb(255,255,255)!important;
    background-color: rgb(0,0,0)!important;
    border-color: rgb(0,0,0)!important;
    box-shadow: 0 0 2px 2px rgb(0,0,0)!important;
}

.btn-success.text-white {
    font-family: Karmatic Arcade;
    font-size: 20px;
    color: rgb(0,0,0)!important;
    background-color: rgb(255,255,255)!important;
    border-color: rgb(0,0,0)!important;
    box-shadow: 0 0 2px 1px rgb(0,0,0)!important;
}
.navbar-brand {
	font-size: 0;
    padding-top: 1px;
    padding-bottom: 1px;
}

.navbar-light .navbar-nav .nav-link:hover {
	color: rgb(0,0,0)!important;
}

.navbar-light .navbar-nav .nav-link {
	color: rgb(0,0,0)!important;
}


.nav-item.nav-link.n-money-text {
	font: bold 14px/1 FontAwesome, system-ui;
    padding-top: 10px;
}

.nav-item.nav-link.n-money-text img {
	content: url("https://i.imgur.com/PmCyCcY.png");
    width: 20px;
    height: 20px;
}

.badge-danger {
    font-family: Comic Sans MS;
    color: rgb(255,255,255)!important;
    background-color: rgb(0,0,0)!important;
    border-color: rgb(0,0,0)!important;
}

.navbar-light.bg-faded {
	box-shadow: 0 1px 2px 0 rgba(0,0,0,.800);
    font-family: Source Sans Pro;
    font-weight: 500;
    padding-top: 0px;
    padding-bottom: 0px;
}

.page-link {
    font-family: Comic Sans MS;
    color: rgb(0,0,0)!important;
