style.css:
@-moz-document domain("en-forum.guildwars2.com") {
    /* Logo */
    
    .Logo img {
        height: 50px;
    }
    
    
    /* Main */
    
    body * {
        font-family: Arial,sans-serif;
    }
    
    
    body .Container .HeaderWrap {
        background-color: #363636;
    }
    
    .FooterWrap .FooterTopWrap {
        background-color: #363636;
    }
    
    .Container .ContentWrap {
        background-color: #363636;
    }
    
    .Container .ContentWrap .Content {
        background-color: #555;
        padding: 5px;
        position: relative;
    }
    
    .BeforeCommentHeading {
        position: absolute;
        top: 0px;
        right: 0px;
    }
    
    body h1, body h2, body h3, body h4 {
        font-family: Verdana;
    }
    .Section-Discussion .Content .PageTitle h1 {
        font-size: 1.8em;
    }
    
    .Section-Discussion .Content .CommentsWrap {
        margin-bottom:0px;
        margin-top:0px;
    }
    
    .Reactions {
        float:right;
    }
    
    
    .Item td {
        padding-top: 0px;
        padding-bottom: 0px;	
    }
    
    
    .Content-art--left,.Content-art--right {
        background-image: none;
    }
    
    .Section-Discussion .Content .Item {
        margin-bottom: 0px;
        width: auto;
        float: none;
    }
    
    .Section-Discussion .Content .Item-Header .PhotoWrap {
        /* display: none; */
    }
    .Section-Discussion .Content .Item-Header .PhotoWrap {
        position: initial;
        top: 0;
        left: 0;
        margin:0px;
    }
    
    .Section-Discussion .Content .Comment {
        margin-bottom: 0px;
    }
    
    .Section-Discussion .Content .Item-Header .PhotoWrap, .Section-Discussion .Content .Item-Header .PhotoWrap img {
        width: 50px;
        height: 50px;
    }
    
    .DataList .Item {
        background: none;
    }
    
    .Section-Discussion .Content .Item-Header:after {
        background-image: none;
    }
    
    .Reactions .ReactButton-Report, .Reactions .ReactButton-Warn, .Reactions .ReactLabel {
        display: none;
    }
    
    .ReactButton {
        margin: 0 0 0 10px;
    }
    
    .Reactions {
        margin: 0px;
    }
    
    .Section-Discussion .ReactMenu {
        margin-left: 0px;
    }
    
    .Section-Discussion .Content .ReactButton.HasCount {
        margin-right: 0px;
    }
    
    .Section-Discussion .Content .Item .Item-BodyWrap {
        background: none;
        border-top: 1px solid #000;
        border-bottom: 1px solid #000;
    }
    
    .Section-Discussion .Content .Item-Header .AuthorTitle a, .Section-Discussion .Content .Item-Header .AuthorTitle span, .Section-Discussion .Content .Item-Header .AuthorWrap a, .Section-Discussion .Content .Item-Header .AuthorWrap span, .Section-Discussion .Content .Item-Header .Rank a, .Section-Discussion .Content .Item-Header .Rank span {
        color: #000000;
        margin-right: 5px;
    }
    
    .Section-Discussion .Content .PageTitle {
        margin-bottom: 0px;
    }
    
    .Section-Discussion .Content .PageTitle:after, .underline-title {
        background: linear-gradient(180deg, rgba(0,0,0,0) calc(100% - 2px), rgba(0,0,0,1) calc(100%), rgba(0,0,0,0) calc(100% + 2px) );
    }
    
    .Container .ContentWrap .MainContent {
        padding: 10px;
    }
    
    .Breadcrumbs {
        margin: 0px;
    }
    
    .Section-Discussion .Content .Item .Item-BodyWrap .Message {
        padding: 0px;
    }
    
    
    /* dark specific */
    
    .HeaderWrap .HeaderBottomWrap .HeaderBottom .HeaderNavDiv .HeaderNav li a.Selected {
        color: #fff;
    }
    .FooterWrap .FooterTop .FooterTopLinks .FooterTopLink--title {
        color: #fff;
    }
    .HeaderWrap .HeaderBottomWrap .HeaderBottom .HeaderNavDiv .HeaderNav li a {
        color: #d5d5d5;
    }
    .FooterWrap .FooterTop .FooterTopLinks .FooterTopLink {
        color: #d5d5d5;
    }
    
    .FooterWrap .FooterTopWrap {
        background-image: none;
    }
    .PromotedContent .Body .BodyLink {
        color: #fff;
    }
    body {
        color: #ccc;
    }
    .CategoryTable tbody .CategoryDescription, .CategoryTable tbody .ChildCategories {
        color: #bbb;
    }
    .Item .BigCount span {
        color: #bbb;
    }
    a.BlockTitle, a.UserLink {
        color: #bbb;
    }
    a {
        color: #bbb;
    }
    body h1, body h2, body h3, body h4 {
        color: #fff;
    }
    .CategoryTable tbody .CategoryDescription a, .CategoryTable tbody .ChildCategories a {
        color: #ccc;
    }
    .PromotedContent .AuthorWrap .Username {
        color: #bbb;
    }
    
    .MenuItems {
        background: #d5d5d5;
    }
    
    .PromotedContent .Title a {
        color: #e1937f;
    }
    .DataTable tbody .CategoryName h2 a, .DataTable tbody .CategoryName h3 a, .Item.Category .Title, .Section-DiscussionList .DiscussionsTable .DiscussionName a.Title, .subtitle-style {
        color: #e1937f;
    }
    .CategoryTable tbody .LatestPost .LatestPostTitle {
        color: #e1937f;
    }
    .Message h1, .Message h2, .Message h3, .Message h4, .Message h5, .Message h6 {
        color: #fff;
    }
    .Message, .message-style, .Preview {
        color: #ddd;
    }
    .ItemDiscussion .Meta, .DataList .Meta {
        color: #bbb;
    }
    .Section-Discussion .Content .Item.administrator-tracker .Meta.InlineTags a, .Section-Discussion .Content .Item.arenanet-tracker .Meta.InlineTags a, .Section-Discussion .Content .Item.arenanetstaff-tracker .Meta.InlineTags a, .Section-Discussion .Content .Item.moderator-tracker .Meta.InlineTags a, .Section-Discussion .Content .Item.Rank-Admin .Meta.InlineTags a, .Section-Discussion .Content .Item.Rank-Mod .Meta.InlineTags a, .Section-Discussion .Content .Item.Role_Administrator .Meta.InlineTags a, .Section-Discussion .Content .Item.Role_ArenaNet .Meta.InlineTags a, .Section-Discussion .Content .Item.Role_Moderator .Meta.InlineTags a {
        color: #e1937f;
    }
    .Message a {
        color: #e1937f;
    }
    .Reactions .ReactButton, .Reactions .ReactButton span , .Reactions .ReactButton.HasCount {
        color: #e1937f;
    }
    .Reactions .ReactButton.Count, .Reactions .ReactButton span.Count {
        background-color: #444;
    }
    .Pager>* {
        background-color: #444;
    }
    .BoxNewDiscussion .Button.Primary:not(.Handle) {
        background-image: linear-gradient(180deg,#e1937f,#e1937f);
    }
    .Pager .Highlight {
        background-color: #e1937f;
    }
    .Section-DiscussionList .DiscussionsTable .DiscussionName .HasNew.HasNew, .Section-DiscussionList .DiscussionsTable .DiscussionName .Meta .Tag.HasNew, .Section-DiscussionList .DiscussionsTable .DiscussionName .MItem.HasNew {
        background-color: #444;
        color: #999;
    }
    .Section-DiscussionList .DiscussionsTable .DiscussionName .HasNew.Tag-Announcement, .Section-DiscussionList .DiscussionsTable .DiscussionName .Meta .Tag.Tag-Announcement, .Section-DiscussionList .DiscussionsTable .DiscussionName .MItem.Tag-Announcement {
        background-color: #333;
    }
    .Section-DiscussionList .DiscussionsTable .DiscussionName .HasNew.tag-tracker, .Section-DiscussionList .DiscussionsTable .DiscussionName .Meta .Tag.tag-tracker, .Section-DiscussionList .DiscussionsTable .DiscussionName .MItem.tag-tracker {
        background-color: #e1937f;
    }
    .BoxNewDiscussion .ButtonGroup .MenuItems li a {
        color: #e1937f!important;
    }
    .BoxNewDiscussion .ButtonGroup .MenuItems {
        background-color: #d5d5d5;
    }
    a.Bookmark:before {
        color: #222;
    }
    .Section-Discussion .Content .Item-Header .AuthorTitle a, .Section-Discussion .Content .Item-Header .AuthorTitle span, .Section-Discussion .Content .Item-Header .AuthorWrap a, .Section-Discussion .Content .Item-Header .AuthorWrap span, .Section-Discussion .Content .Item-Header .Rank a, .Section-Discussion .Content .Item-Header .Rank span {
        color: #fff;
    }
    .Section-Discussion .Content .DataList .Meta a:not(.Tag), .Section-Discussion .Content .ItemDiscussion .Meta a:not(.Tag) {
        color: #e1937f;
    }
    .Buttons .Back a:active, .Buttons .Back a:focus, .Buttons .Back a:visited, .js-change-picture-form .Back a:active, .js-change-picture-form .Back a:focus, .js-change-picture-form .Back a:visited {
        color: #e1937f;
    }
    .editor-help-text a {
        color: #e1937f;
    }
    .Section-Discussion .Content .DataList .Meta a:not(.Tag), .Section-Discussion .Content .ItemDiscussion .Meta a:not(.Tag) {
        color: #e1937f;
    }
    code, pre {
        background: #ffe;
        color: #000;
    }
    .Section-Discussion .Content .Item-Header .AuthorTitle a, .Section-Discussion .Content .Item-Header .AuthorTitle span, .Section-Discussion .Content .Item-Header .AuthorWrap a, .Section-Discussion .Content .Item-Header .AuthorWrap span, .Section-Discussion .Content .Item-Header .Rank a, .Section-Discussion .Content .Item-Header .Rank span {
        color: #fff;
    }
    .Section-Discussion .Content .Item.Role_Member .Item-Header {
        background: none;
    }
    
    .Reactions .ReactButton.HasCount .Count {
        background-color: None;
    }
    
    .Section-Discussion .Content .Discussion .Options {
        top: 36px;
    }
    
    .Section-Discussion .Content .Item.ItemDiscussion {
        margin-bottom: 0px;
    }
    
    .breadcrumb-style, .Breadcrumbs a span, .Breadcrumbs a span:active, .Breadcrumbs a span:focus, .Breadcrumbs a span:visited, .Buttons .Back a, .Buttons .Back a:active, .Buttons .Back a:focus, .Buttons .Back a:visited, .js-change-picture-form .Back a, .js-change-picture-form .Back a:active, .js-change-picture-form .Back a:focus, .js-change-picture-form .Back a:visited {
        color: #ccc;
    }
    
    .Reactions .ReactButton.HasCount .Count {
        background-color: #444;
    }
    .Section-Discussion .Content .Item-Header .Meta a, .Section-Discussion .Content .Item-Header .Meta span, .Section-Discussion .Content .Item-Header .Meta time {
        color: inherit;
    }

    .Section-Discussion .MessageList .Item .CommentHeader, .Section-Discussion .MessageList .Item .DiscussionHeader, .Section-Discussion .Content .Item .Item-BodyWrap {
        margin-left: 0px;
        width: auto;
    }
}
