style.css:
@-moz-document regexp("(https:\/\/|http:\/\/|)(www\.|)lesyt\.xyz.*") {
div  {background-color: #0d0d0d !important;color: #aaa !important;}
body {background: none !important;background-color: #0d0d0d !important; color: #aaa !important;}
a  {background: none !important;background-color: #0d0d0d !important; color: #C9C4C4;}  
a:hover {color: #C4B88A;}
p  {background: none !important;background-color: #0d0d0d !important; color: #888;}
li {background: none !important;background-color: #0d0d0d !important; color: #888;}
site-navigation {background: none !important;}
site-navigation {background-color: #131313 !important;}
comments {background-color: #241d1d !important; color: #afafaf;}
.sp-wrap{background: none; background-color: #3c3c3c; border-color: #C9C4C4;}
.sp-body {background: none !important;background-color: #0d0d0d !important;}
.stacked-list {background: none !important;background-color: #0d0d0d !important;}
.stacked-list-item {background: none !important;background-color: #0d0d0d !important;}
.stacked-list-item:hover {background: none !important;background-color: #C4B88A !important;}
.stacked-list-item.collapsible {background: none !important;background-color: #282828 !important;}
.stacked-list-item .collapsible-header{background-color: #0d0d0d !important;}
}
