/*
Title: Blue and Black Messenger
Version: 3.1
Credits: Jonathan
*/

:root {
    --animation-duration: 0.3s;
    --gradient-len: 100%;
}

@keyframes duck {
    from {fill:rgba(0, 223, 187);}
    to {fill:transparent;}
}

@keyframes duckHide {
    from {
        background: rgba(0,0,0,0.5);
        border-radius: 32px;
        padding: 2.5px;
    }
    
    to {
        background: rgba(0,0,0,0);
        border-radius: 32px;
        padding: 0px;
    }
}

@keyframes duckShow {
    from {
        background: rgba(0,0,0,0);
        border-radius: 32px;
        padding: 0px;
    }
    
    to {
        background: rgba(0,0,0,0.5);
        border-radius: 32px;
        padding: 2.5px;
    }
}

@keyframes spreadOut {
    from {
        margin-right: 2.5px;
    }
    
    to {
        margin-right: 6px;
    }
}

@keyframes spreadIn {
    from {
        margin-right: 6px;
    }
    
    to {
        margin-right: -5px;
    }
}

@keyframes motionSlide {
    0%{background-position:10% 0%}
    50%{background-position:91% 100%}
    100%{background-position:10% 0%}
    
