style.css:
@-moz-document url-prefix("https://foxbit.exchange/"){
/*Topo cabeçalho*/

.head-top{
	background-color: #848484 !important;
}

.head-top-button{
	background-color: #848484 !important;
	color: #000;
}

.head .navbar{
	background-color: #484848;

}

.balance-currency__amount{
	color: aliceblue !important;
}

.head .nav li{
	color: aliceblue !important;
}

.head .nav li a, .head .nav li .order .order__cell:last-child .orders__head .dropdown-menu .btn-show-cum-qty, .order .order__cell:last-child .orders__head .dropdown-menu .head .nav li .btn-show-cum-qty, .head .nav li .order .order__cell:last-child .orders__head .dropdown-menu .btn-show-fees, .order .order__cell:last-child .orders__head .dropdown-menu .head .nav li .btn-show-fees{
	color: aliceblue !important;
}

.order{
	background-color: #848484;
}

/* Livro de Ofertas */

.page{
	background-color: #848484;
	color: aliceblue;
}

.page__content{
	background-color: #484848 !important;
        border-radius: 10px;
}

.page__title{
	color: #e8683e;
}

.col-md-6{
	color: aliceblue;
	background-color: #848484;
}

.locked-balance-display{
	background-color: #848484;
}

.order-form__mid{
	color: #000;
}

/* Meu Perfil */

.table>thead>tr>th, .table>thead>tr>td, .table>tbody>tr>th, .table>tbody>tr>td, .table>tfoot>tr>th, .table>tfoot>tr>td{
	background-color: #848484;
}

.page__section_white{
	background-color: #848484 !important;
}

/* tweet */

.timeline-Footer{
	background-color: #31495a;
}

.SandboxRoot.env-bp-min .timeline-Tweet{
	background-color: #31495a;
	color: aliceblue;
}

.timeline-InformationCircle-widgetParent{
	background-color: #31495a; 
}

/* Histórico de ordens */

.fuelux .table td{
	background-color: #848484;
}

.table-bordered>thead>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>tfoot>tr>td{
	background-color: #848484;
}

.btn{
	color: #000;
}

/* rodapé */

.foot{
	background-color: #e8683e;
}

.order .order__cell:first-child .orders__head{

 background-color: #5cb85c;
}

.order .order__cell:last-child .orders__head{

background-color: #d9534f;
}

.thumbnail{
background-color: #848484 !important;
}
}
