style.css:
@-moz-document domain("tumblr.com") {
/**                   ----------------------------------------- MAIN THEME -----------------------------------------                   **/
/** Scroll Bar **/

body::-webkit-scrollbar {
    width: 1em;
}
body::-webkit-scrollbar-thumb {
    background-color: #EBB6FE;
    outline: 1px solid #EBB6FE;
}
/** Font **/

a,
p,
h1 {
    font-size: 95%;
}
body {
    color: #000000;
}
.post-content-text blockquote,
.post-content-text figure,
.post-content-text hr,
.post-content-text ol,
.post-content-text p,
.post-content-text pre,
.post-content-text ul,
.post .post_body blockquote,
.post .post_body figure,
.post .post_body hr,
.post .post_body ol,
.post .post_body p,
.post .post_body pre,
.post .post_body ul,
.reblog-content blockquote,
.reblog-content figure,
.reblog-content hr,
.reblog-content ol,
.reblog-content p,
.reblog-content pre,
.reblog-content ul,
.post_full .post_header .post_info .post_info_link:first-child,
.post.new_post .new_post_label,
.reblog-list-item .reblog-tumblelog-name,
.search_controls .search_control_button,
.search_controls .search_control_link {
    color: #000000!important;
}

.search_sub_header .search_term_heading, 
.search_sub_header .search_control.related_searches .search_control_link,
.search_sub_header .search_control.related_searches .related_searches_label{
	color: #000000;
}
/** Xkit **/

.xtag .result_title,
.xtag {
    color: rgba(12, 12, 12, 1);
}
#post_limit_checker_view:after {
    filter: contrast(100%);
}
.identity .controls_section a .count,
.identity .controls_section li.section_header {
    color: rgba(0, 0, 0, 1)!important;
}
/** Main Background **/
/** Avatars **/

.post_avatar 
.post_avatar_link {
    background-color: #F8F8F8;
}

.identity,
.post_avatar,
.l-container.l-container--two-column-dashboard .left_column,
.l-container.l-container--two-column-dashboard .l-content,
.l-container.l-container--two-column .l-content,
.l-container.l-container--two-column-dashboard .right_column,
.l-container.l-container--two-column .right_column,
.l-container.l-container--flex .l-content,
.search_results_container .tumblelog_mask_item {
    background: #F8F8F8;
}
.post-forms-glass,
.blue#glass_overlay,
.blue#hello_glass,
.blue.glass,
.glass {
    background-color: rgba(204, 204, 204, 0.95);
}
.identity .controls_section.user_list li .blog.has_follow_button:before,
.remnant_ad,
.controls_section_radar,
.radar,
.sidebar_link,
.identity .right_column:after {
    display: none!important;
}
.post_full .post_permalink,
.post_full:hover .post_permalink {
    border-color: rgba(0, 0, 0, 0) rgba(0, 0, 0, 0) #e2e2e2 #e2e2e2;
    background-color: rgba(0, 0, 0, 0);
}
#discover_actions_discover .elevator,
#search_actions_search .elevator {
    background-color: rgba(0, 0, 0, 0)!important;
}
.rapid-recs-container .rapid-recs {
    background-image: none;
}
.toastr .toast .toast-bread,
#toaster .toast .toast_border,
#toaster .toast .toast_border,
.notification,
.notification:hover,
.notification.control_hover:active,
.noun .avatar .item,
.toastr .toast {
    background-color: black;
    /*color: black;*/
}
/** New Post **/

.post.new_post {
    background-color: #F8F8F8;
}
.new_post_label i,
.tab-post-type .post-type-icon {
    filter: brightness(140%);
}
.post.new_post .new_post_label {
    background: none;
}
/** Posts Background **/

.post.post_full,
.post .post-source-footer,
.post_full .post_wrapper,
.peepr-body .indash_blog .post,
.post_full.is_conversation .conversation_lines .chat_line:nth-child(even),
.peepr-body .indash_blog .post_chrome.chat .conversation_lines .chat_line:nth-child(2n),
.post-content-text blockquote,
.post .post_body blockquote,
.reblog-content blockquote {
    background-color: #F8F8F8;
}
.reblog-list-item,
.reblog-list,
.reblog-trail--original-post {
    border-bottom: #F8F8F8;
    border-top: #F8F8F8;
}
/** Ask Post Background **/

.post-composer_note-post .note_item,
.post_full.is_note .post-body .note_item,
.post_full.is_note .post_body .note_item,
.peepr-body .indash_blog .post_chrome.answer .post_body .note_item {
    background-color: #f6f6f6;
    border-color: #f6f6f6;
}
.post_full.is_note .nipple,
.post-composer_note-post .nipple,
.peepr-body .indash_blog .post_chrome.answer .nipple {
    border-left: 8px solid #f6f6f6;
}
/** Recomemmended Blogs to Follow **/

.plus-follow-button {
    font-size: 20px!important;
}
.icon_plus:before,
.plus-follow-button .follow_icon,
.plus-follow-button:before,
.plus-follow-button.blue,
.plus-follow-button:hover {
    background: none!important;
    font-size: 20px!important;
}
/** buttons **/
/** discover search **/

.discover .discover-search-term-item .discover-search-term {
    filter: brightness(140%);
}
}

