@-moz-document domain("tumblr.com") {
/*                                                         MADE BY @MILEMORALES
	                                                      DO NOT STEAL THANKS!!! 
*/
/** New Post Buttons 

See Instructions if you want to change the buttons: https://userstyles.org/styles/140059/tumblr-gradient-header
**/
/*.tab-post-type .post-type-icon {

    background-image: url(http://i.imgur.com/rqwbJqB.png);

}*/
/** Gradient Header 
	
	- If you want a box shadow, just comment "box-shadow: none" out
**/

.l-header-container--refresh {
    background: linear-gradient(to right, #FE8F6A, #FED952, #85FEC6, #80DFFE, #EBB6FE)!important;
    /** header gradient **/
    
    box-shadow: none;
    /** no shadow/line under header **/
}
/** Follow Text 
	changes the color of the follow text that is 	shows up next to a user if you don't follow them
**/

.follow-text {
    color: #EBB6FE!important;
}
/** Tumblr Balloon Text Color **/

.identity-refresh .tab_notice .tab_notice_value {
    background: rgba(0, 0, 0, 0)!important;
    /**  background color **/
    
    color: #fff!important;
    /** text color **/
}
/** Tumblr Balloon Background Color **/

.l-header-container--refresh .tab_notice,
.identity-refresh .new_post_notice_container {
    background: linear-gradient(to top, rgba(239, 198, 214, .8), rgba(102, 74, 133, .1))!important;
    /** background color **/
    
    border-color: rgba(255, 255, 255, .5)!important;
    /** outline color **/
}
/** New Post Button Color **/

.compose-button {
    fill: #ffffff!important;
}
/** New Post Button Stroke Color **/

path {
    stroke: rgba(255, 255, 255, 0)!important;
}
}
