style.css:
@-moz-document domain(site.dashboard.italiaonline.it) {

/* hide image previews in brief */

#contentImageUploader > div > div.dz-image > img {
    display: none !important;
}
#contentImageUploader > div:hover > div.dz-image > img {
    width: 100% !important;
    display: inline-block !important;
    filter: none !important;
    -devkit-filter: none !important;
}
body,
#submitButtonDiv,
.order-container.sticky-footer {
    background-color: #222;
}
.DisabledBrief,
#content {
    background-color: #333;
}
#content > section > div > div > a {
    color: #fff;
}
#content > section > div > div > a::after {
    border-top: 4px solid #dedede;
}
.DisabledBrief label,
H3 {
    color: #dedede;
}
.order-productHeader,
.order-productGroupHeader,
.option-buttons.link-button,
#btnSaveProgress,
#btnFinish {
    background-color: rgb(255, 0, 92);
    color: #fff;
}
input,
textarea,
.dropzone,
.dz-image-preview {
    background-color: #212121 !important;
    color: #dedede !important;
}
}
