style.css:
@-moz-document domain("nexusmods.com") {
/*Word of Warning - I'm a designer (not even a web one at that!)not a coder so this 
stylesheet is a little rough. But it works so ¯\_(ツ)_/¯*/

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [OPTIONAL] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Uncomment this to enable*/

/**Remove Media section from homepage**/

/*
@media all {
    .static.gameindex .wrap:nth-of-type(3) {
        display: none;
    }
}
*/

}

@-moz-document domain("nexusmods.com") {
/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [WRAPPER] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
body {
    text-rendering: auto!important;
}
body::after {
    content: ' ';
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-color: #02070680;
    background-position: center top;
    background-size: cover;
    background-repeat: no-repeat;
    will-change: transform;
    z-index: -1;
}

/*Remove static wrap padding*/
.static.homeindex .wrap,
.static.gameindex .wrap {
    background: var(--mainbg)!important;
}
.static .wrap {
    background: var(--mainbg)!important;
    padding: 0;
}
.static .wrap .col-1-1.static-head,
.static .wrap .featured {
    padding: 0 20px;
    margin: 15px 0 10px 0;
}
.modpage > .wrap {
    background: var(--mainbg);
    background-image: none;
}
.title-left .wrap {
    padding: 0;
}

/*Background for media page*/
.col-1-1 > .container,
.modpage.mediapage .container.comments,
.modpage .col-1-1 {
    background: var(--tabcontent);
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [BUTTONS] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Fix small button padding*/
a.btnsmall,
.btn.btnsmall {
    font-size: 11px;
    padding: 5px 8px;
}

/*Fix big button padding*/
a.btn,
.btn {
    padding: 8px 10px;
}

/*Fix active button padding*/
a.btn.inline-flex.btn-active {
    padding: 7px 10px
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [TEXT] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
p {
    margin: 0 0 15px 0;
    line-height: 1.5;
    font-weight: 400;
    letter-spacing: 0.2px
}
h1 {
    color: #e6e6e6;
}
h2,h3,h4,h5,h6 {
    color: #e1e1e1;
}
.modpage .wrap, .static .wrap {
	font-weight: 400;
}
/*Change thickness of Nexus homepage body text*/
.home-intro p#opening {
    font-weight: 300;
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [DIRECTORY ADDRESS] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
ul#breadcrumb.mod-crumb,
ul#breadcrumb.static-crumb {
    text-shadow: 0 1px 4px rgba(0, 0, 0, 1);
    font-weight: 400;
    font-size: 12px;
}

/*Hide directory to self*/
ul#breadcrumb.mod-crumb li:last-of-type,
ul#breadcrumb.static-crumb li:last-of-type {
    display: none
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [POPUP] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Reduce mod name in endorsement reminder*/
#endorsement-reminder-list li .reminderText {
    font-size: 14px;
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [TAB] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
ul.modtabs {
    display: flex;
    background: var(--mainbg);
    padding-left: 6px;
}
.static.homeindex ul.modtabs,
.static.gameindex ul.modtabs,
.modpage:not(.title-left) ul.modtabs {
    border-top: 0px solid var(--tabborder);
}
ul.modtabs li a {
    display: flex;
    align-items: center;
    height: 45px;
    font-size: 12px;
    border-right: 1px solid var(--mainbg);
    padding: 0 15px;
    color: #e1e1e1;
}
ul.modtabs li a:hover {
    border-right: 1px solid var(--mainbg)!important;
    background: var(--tabhover);
}
ul.modtabs li a.selected {
    border-right: 1px solid var(--tabborder);
    background: var(--tabcontent);
    font-weight: 500;
}
.tab-label {
    white-space: normal;
}

/*Reduce tab content text size*/
.tabcontent {
    font-size: 14px;
    letter-spacing: 0.2px;
    color: #e1e1e1;
    background: var(--tabcontent);
}

/*Add some padding to loading wheel*/
.tabcontent:before,
.loading-wheel {
    margin: 20px auto;
}
.small-loading-wheel {
    margin: 4px;
}

/*Tab counter*/
span.alert {
    font-size: 11px;
    padding: 2px 4px 2px 4px;
    top: -1px;
    vertical-align: middle;
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [ACCORDION] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
.accordion dt {
    color: var(--theme-color)!important;
    background: var(--headerlight)!important;
    padding: 8px 15px!important;
}
.accordion dt .acc-status {
    width: 22px;
    height: 22px;
    position: absolute;
    top: 7px;
    right: 7px;
    background: none!important;
}
.accordion dt .acc-status .icon-arrow {
    fill: var(--theme-color);
}
.accordion dt:hover {
    background: #333!important;
}
.accordion dd {
    background: var(--contentdark);
    padding: 15px;
}
#mod_files .accordion dd {
    background: var(--tabletd)!important;
}

/*Put some space between open accordions*/
.accordion dd.clearfix.open {
    margin-bottom: 0.25em!important;
}
.accordion dd h3 {
    font-size: 14px;
}
.accordion dd p {
    font-size: 13px
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [TABLES] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
.table th {
    background: var(--headerlight);
    border-right: 1px solid var(--contentlighta)!important;
    border-bottom: 0!important;
}
.table th:last-of-type {
    background: var(--headerlight);
    border-right: 0!important;
    border-bottom: 0!important;
}
.table th:hover {
    background: var(--headerdark)!important;
}
th.table-download,
td.table-download,
th.table-update,
td.table-update {
    width: 110px;
}
.table td {
    background: var(--tabletd)!important;
    border-right: 1px solid var(--contentlighta)!important;
    border-top: 1px solid var(--contentlighta)!important;
    border-bottom: 0!important;
}
.table td:last-of-type {
    border-right: 0!important;
}
#mod-editing-content .table td {
    background: var(--tabletd)!important;
    border-right: 1px solid var(--contentdark)!important;
    border-top: 1px solid var(--contentdark)!important;
    border-bottom: 0!important;
}
#mod-editing-content .table th {
    border-right: 1px solid var(--contentdark)!important;
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [TILES] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Fix plus icon which misaligns for some weird reason*/
.icon-plus {
    margin: 0 0 0 1px;
}

/*Fix image button misalignment*/
.delete.inline-flex a,
.edit.inline-flex a {
    height: 15px;
}

/*Set thumbnail to fill container*/
figure.image_figure .fore_div img,
figure.image_figure .fore_div_mods img,
.fore_div_mods img {
    height: 100%;
    width: 100%;
    display: flex;
    object-fit: cover;
    flex: 1;
    overflow: hidden;
}

/*Give tiles more contrast*/
ul.tiles.tile-list li.mod-tile .mod-tile-right,
ul.tiles li .tile-desc {
    /*background: var(--tilebg);*/
    background: linear-gradient(to bottom, rgba(39, 39, 39, 0) 0, rgba(39, 39, 39, 1) 100%)
}

ul.tiles li .tile-data,
ul.tiles li a.mod-image {
    background: var(--tilefooter);
}

/*Reduce height of text fade off*/
ul.tiles li .tile-desc .fadeoff {
    height: 20px;
    background: -webkit-linear-gradient(top, rgba(39, 39, 39, 0) 0, rgba(39, 39, 39, 1) 100%);
    background: linear-gradient(to bottom, rgba(39, 39, 39, 0) 0, rgba(39, 39, 39, 1) 100%);
}

/*Title font size*/
ul.tiles li .tile-desc h3 a {
    font-size: 14px;
}

/*Tile description*/
ul.tiles li .tile-desc p {
    font-size: 12px;
    color: #aaa
}

/*Change hovered tile overlay to white*/
ul.tiles li .view-content {
    background: -webkit-linear-gradient(bottom, rgba(255, 255, 255, 0) 0, rgba(255, 255, 255, 0.1) 100%);
    background: linear-gradient(to top, rgba(255, 255, 255, 0.05) 0, rgba(255, 255, 255, 0.1) 100%);
}

/*Tweak hovered 'VIEW' button slightly*/
ul.tiles li .view-label {
    /*padding: 5px 8px;
    background: none!important;
    top: 45%;
    font-weight: 300!important;
    font-size: 10px;*/
    display: none;
}

/*News tag position*/
ul.tiles li.article-tile .single-image a.post-category {
    position: absolute;
    top: 0;
    left: 0;
}

/*Tile expando border*/
ul.tiles li .btnexpand ul li {
    border-top: 1px solid #ffffff30!important;
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [FORMS] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Option sections*/
.form-section-content {
    background: var(--contentdark);
    border-bottom: 1px solid var(--contentlighta);
}
.form-section-content .col-1-1 {
    background: transparent;
}
.form-section header {
    background: var(--headerlight);
}

/*Text field text color*/
input.form-field {
    color: #4e4e4e;
}

/*I don't remember what this does*/
select.form-field {
    color: #4e4e4e;
}

/*Add white border around dropdown items*/
.select2-dropdown,
.select2-dropdown--below {
    border: 1px solid #fff
}

/*Drop box*/
ul.tile-upload li .tile-image,
.readme-canvas,
.canvas {
    background: var(--imagebg);
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [ARROW LIST] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Used in activity tracker and mod manager*/

/*> List*/
ul.arrowlist li {
    line-height: 1.5;
    font-size: 12px;
    font-weight: 400
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [ACTIVITY TRACKERS] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
ul.activity > li {
    margin: 0 0 5px 0;
}

/*Remove side bar*/
.activity-point {
    display: none;
}
.activity-desc {
    padding: 0 0 10px 0;
    background: var(--tabletd);
}

/*Set the date as header*/
.activity-desc .activity-date {
    padding: 6px 10px;
    font-size: 12px;
    display: block;
    width: 100%;
    background: var(--headerlight);
}

/*Fix padding*/
.activity-desc ul.activity-inner {
    padding: 5px 10px;
}

/*Increase minimum height of tracker item*/
.activity-desc .activity-extra {
    max-height: 110px;
}

/*Reduce text fade off*/
div.activity-desc div.fadeoff {
    background: -webkit-linear-gradient(top, rgba(45, 45, 45, 0) 30%, rgba(45, 45, 56, 1) 50%);
    background: linear-gradient(to bottom, rgba(45, 45, 45, 0) 30%, rgba(45, 45, 45, 1) 100%);
}
a.btn.btn-load.load-more-activity {
    margin-top: 12px;
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [RECENT ACTIVITY] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Fix page padding, which somehow uses .tabcontent*/
.apage.newrecently .tabcontent {
    padding: 20px!important;
    /*It also has inline padding set..*/
}

/*And uses its own pagination..*/
.recent-pagination:first-of-type {
    margin-top: 0;
}
.recent-pagination:last-of-type {
    margin-bottom: 0;
}

/*Change list colors*/
.recent-files a {
    background: var(--tabletd);
}
.recent-files a:hover {
    background: var(--headerlight);
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [PAGINATION] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
.pagination ul li a.page-selected,
.pagination ul li a.page-selected:hover {
    color: #e1e1e1!important;
    background: var(--mainbg);
}

/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [SITE FOOTER] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/
#sitemap {
    background: var(--siteheader)
}
#sitemap ul.footlinks li {
    list-style: none;
    font-weight: 500;
    margin: 0 0 5px 0
}
#sitemap ul.footlinks li a {
    font-size: 12px;
    color: #bbb
}
#sitemap .footrow h3 {
    font-size: 14px;
}
#sitemap .footrow .stat {
    font-size: 12px;
}
#follow {
    background: var(--headerdark);
    padding: 10px 15px 5px 15px;
}
#follow h3 {
    font-size: 14px;
    color: #ccc;
    top: -12px
}
#footer ul#social li {
    font-size: 14px;
    margin: 0 20px 0 0
}
#footer ul#social li a {
    color: #ccc
}
#footer ul#social li a:hover {
    opacity: 1;
    color: #fff;
}
#foot {
    background: var(--siteheader);
}
@media(max-width:1280px) {
    .wrapper {
        max-width: 960px;
        padding: 0
    }
    .static .wrap:first-of-type {
        background: var(--mainbg);
    }
}
@media(max-width:980px) {
    .wrapper {
        padding: 0
    }
}
}

@-moz-document domain("nexusmods.com") {
/*▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄ [HOME PAGE] ▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀▄▀*/

/*Fix padding on news page*/
ul.featured-articles {
    list-style: none;
    margin: 0 20px 20px 20px;
}

/*Reorder subsections*/
.static.gameindex {
    display: flex;
    flex-direction: column;
}
.static.gameindex .wrap:nth-of-type(1),
.static.gameindex .wrap:nth-of-type(2),
.static.gameindex .wrap:nth-of-type(4) {
    order: 1
}
.static.gameindex .wrap:nth-of-type(3) {
    order: 2
}

/*Section titles*/
.static .wrap:first-of-type .col-1-1 .tab-head {
    margin: 20px 0 10px 0;
}
.static .wrap .col-1-1 .tab-head {
    padding: 0 20px;
    margin: 25px 0 10px 0;
}
.static .wrap .col-1-1 .tab-head h2 {
    font-size: 32px;
}

/*"Explore" link*/
a.view-all {
    margin: 3px 0 0 10px;
    color: #e1e1e1;
    float: right;
}
a.view-all::before {
    /*add a little 'clickable' indicator*/
    content: '\00bb ';
    margin-right: 3px;
    font-size: 15px;
    line-height: 1;
}

/*Give some room to 'VIEW MORE' button, excessively butchering layouts in the process*/
.static .tabcontent,
.static .tabcontent #news-container,
.static .tabcontent .container.tab-articles {
    display: flex!important;
    flex-direction: column;
}
.static:not(.resultpage) #mod-list.container,
.static:not(.resultpage) #media-list.container,
.static:not(.resultpage) #news-content.container,
.container.tab-articles {
    padding-bottom: 0;
}
#tab-modarticles .tiles {
    padding-top: 0;
}

/*done butchering*/
.static.gameindex .btn-more,
.static.homeindex .wrap .btn-more {
    order: 999!important;
    display: inline-block;
    position: relative;
    margin: 0 0 20px 0;
}

/*Match home index fade off with our bg color*/
.fade-off::after {
    background: -moz-linear-gradient(top, rgba(32, 32, 32, 0) 0, rgba(32, 32, 32, 1) 100%);
    background: -webkit-linear-gradient(top, rgba(32, 32, 32, 0) 0, rgba(32, 32, 32, 1) 100%);
    background: linear-gradient(to bottom, rgba(32, 32, 32, 0) 0, rgba(32, 32, 32, 1) 100%);
}

/*Hide extra tiles*/
.static.homeindex .wrap:nth-of-type(2) .tabcontent ul.tiles li:nth-of-type(n+9),
.static.homeindex .wrap:nth-of-type(3) .tabcontent ul.tiles li:nth-of-type(n+11) {
    display: none
}
.static.gameindex .wrap:nth-of-type(2) .tabcontent ul.tiles li:nth-of-type(n+11),
.static.gameindex .wrap:nth-of-type(3) .tabcontent ul.tiles li:nth-of-type(n+9),
.static.gameindex .wrap:nth-of-type(4) .tabcontent ul.tiles li:nth-of-type(n+9) {
    display: none
}

/*Trending tiles*/
ul.tiles li.image-tile .tile-desc {
    text-shadow: 0 0 3px rgba(0, 0, 0, 1);
    background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0) 0, rgba(0, 0, 0, .95) 100%);
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0, rgba(0, 0, 0, .95) 100%);
}
.home-trending ul.tiles li.image-tile .tile-desc p.mod-tile-desc {
    max-height: 3em;
}
.home-trending ul.tiles li.image-tile .tile-data {
    display: none!important;
}

/*Increase tile title length*/
.home-trending ul.tiles li.image-tile h3 {
    max-width: 100%!important;
}

/*Hide categories*/
.home-trending ul.tiles li.image-tile ul.inline-list li:nth-of-type(1) {
    display: none!important;
}

/*Fallback - set description to 2 lines*/
.home-trending ul.tiles li.image-tile:nth-of-type(1) .tile-desc {
    display: block!important;
}
@media(min-width:981px) {
    .home-trending {
        margin: 0;
        padding: 15px 25px 15px 25px;
        background-color: var(--gallerybg);
    }
    .home-trending a.mod-image {
        background: var(--imagebg);
    }
    .home-trending ul.tiles {
        display: grid!important;
        overflow: hidden;
        grid-template-columns: 20% auto auto auto auto;
        grid-template-rows: repeat(2, auto);
        grid-template-areas: "main main child2 child3 child4" "main main child5 child6 child7";
        grid-column-gap: 15px;
        grid-row-gap: 15px;
        align-items: center;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(1) {
        grid-area: main;
        width: auto;
        margin: 0;
        padding: 0;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(2) {
        grid-area: child2;
        width: auto;
        margin: 0;
        padding: 0;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(3) {
        grid-area: child3;
        width: auto;
        margin: 0;
        padding: 0;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(4) {
        grid-area: child4;
        width: auto;
        margin: 0;
        padding: 0;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(5) {
        grid-area: child5;
        width: auto;
        margin: 0;
        padding: 0;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(6) {
        grid-area: child6;
        width: auto;
        margin: 0;
        padding: 0;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(7) {
        grid-area: child7;
        width: auto;
        margin: 0;
        padding: 0;
    }
    .home-trending ul.tiles li.image-tile .tile-desc {
        max-height: 100%;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(n+2) ul.inline-list li:nth-of-type(1) {
        width: 95%;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(n+2) ul.inline-list li:nth-of-type(1)::after {
        display: none;
    }
    /*Rearrange first tile description*/
    .home-trending ul.tiles li.image-tile:nth-of-type(1) .tile-desc {
        display: flex!important;
        flex-direction: column;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(1) h3 {
        order: -1;
        margin-bottom: 0;
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(1) ul.inline-list {
        order: -1;
        margin-top: 0;
        margin-bottom: 10px;
    }
    /*Increase max height of description*/
    .home-trending ul.tiles li.image-tile .tile-desc p.mod-tile-desc {
        max-height: 6em;
    }
    /*Change header for the 2nd tile*/
    .home-trending ul.tiles li.image-tile:nth-of-type(n+2) h3 {
        max-width: 100%;
        font-family: "Roboto";
        font-size: 14px;
        margin: 0 0 3px 0;
        font-weight: 500;
        line-height: 1.2
    }
    .home-trending ul.tiles li.image-tile:nth-of-type(n+2) ul.inline-list {
        margin-top: 0px;
    }
