style.css:
@-moz-document regexp("^chrome-extension://.+manage.html$") {
body {
    background: #000;
	color: #aaa;
}
* {border-color: #235 !important}
#installed::after {background: none}

input {
    background: #224;
    padding: 5px 7px !important;
	font-size: 13.5px;
    font-weight: 300;
    letter-spacing: .35px;
    color: #fff;
}
::-webkit-input-placeholder {color: #835d5d}
::-moz-placeholder {color: pink}
input[type="checkbox"] {filter: invert(.9) brightness(1.8)}

h1 {
    background: #f54;
    color: #000;
    display: inline-block;
    padding: 2px 7px;
}
h2 {color: #ca8}
p {color: #966}
button {
    background: #6060a0;
    border: none;
    color: #000;
    font: 12.3px "Lucida Sans Unicode", "Lucida Grande", sans-serif;
    padding: 3px 7px
}
button:hover {
    background: #fc2;
    color: #000;
    cursor: pointer
}

}

@-moz-document domain("example.com") {

}
