style.css:
@-moz-document domain("www.fasttech.com"), domain("support.fasttech.com") {
/* 
    General things
*/

body {
    color: #CACACA !important;
    background: -webkit-radial-gradient(#555555, #383838) !important;
    background: radial-gradient(#555555, #383838) !important;
    /* background-color: #444444 !important; */
    
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
}
#navbar,
#navbar tr.navbarsearch,

.goog-te-gadget-simple,
#footer {
    background: transparent !important;
}
#searchbar {
    padding-right: 25px;
}
#SearchBarSearchButton {
    background-color: #444444 !important;
}
#searchBarOptionsCell,
#searchBarCategory,
#categoryPopout {
    background-color: #666666 !important;
    background-image: none !important;
}
#searchButtonCell,
#searchBoxCell {
    background-image: none !important;
    background-color: #777777 !important;
}
.PageTitle,
#content_ProductTitle {
    color: #e5e5e5 !important;
}
a,
a:visited,
.ThreadRowSubline a.ThreadLink,
.ThreadRowSubline a.Threadlink:visited {
    color: #cacaca !important;
}
a:hover {
    color: #0D7BAD !important;
}
.ThreadRowSubline a.Threadlink:hover,
.ThreadRowSubline a:hover {
    color: #ca2345 !important;
}
/* Buttons */

.FormButton {
    box-shadow: none !important;
    border: 0 !important;
    padding: 3px 15px !important;
    text-shadow: 0 2px 1px rgba(0, 0, 0, 0.5) !important;
}
/* Only the search button */

.FormButton.blue,
.FormButton.orange {
    background: none !important;
    background-color: #0989c4 !important;
    color: white !important;
}
.FormButton.blue:hover,
.FormButton.orange:hover {
    background-color: #0a93d3 !important;
}
.FormButton.green {
    background: none !important;
    background-color: #00870b !important;
    color: white !important;
}
.FormButton.green:hover {
    background-color: #00a70e !important;
    color: white !important;
}
.FormButton.red {
    background: none !important;
    background-color: #a10d2b !important;
    color: white !important;
}
.FormButton.red:hover {
    background-color: #c61035 !important;
    color: white !important;
}
.FormButton.white {
    background: none !important;
    background-color: #EDEDED !important;
    color: #302f2f !important;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.1) !important;
}
.FormButton.white:hover {
    background-color: #FFFFFF !important;
    color: #302f2f !important;
}
.Pros .FieldFlag {
    background-color: #00870b !important;
}
.Cons .FieldFlag {
    background-color: #a10d2b !important;
}
/* Replacing NAV buttons */

#CommunityButton,
#CartButton,
#SupportButton {
    background-image: url('https://osigalla-cdn.sirv.com/FTDark/E9X5FTa.png') !important;
}
#SupportButton {
    padding: 0 5px 0 8px !important;
}
#CommunityButton {
    padding: 1px 5px 0px 10px !important;
}
#CartButton {
    padding: 0px 5px 0px 10px !important;
}
/* The searchbox where you actually type */

#searchBoxCell {
    padding-left: 10px !important;
}
.searchbox {
    color: #FFFFFF !important;
}
/* Search All beside searchbox. */
/* First fix the width */

#searchBarOptionsCell,
#CategorySelectorFrame,
#searchBarCategory {
    width: 100px !important;
    padding-left: 7px !important;
}
/* #searchBarOptionsCell {
    background-color: #666666 !important;
    background-image: none !important;
} */
/* The selector for search all */

#searchBarCategory {
    color: #CACACA !important;
    -moz-appearance: none !important;
    text-indent: 0.01px !important;
    text-overflow: '' !important;
}
select#searchBarCategory option {
    background-color: #555555 !important;
    border: 0 !important;
    font-size: 10pt !important;
}
/* Search button */
/* #searchButtonCell {
    background-image: none !important;
    background-color: #777777 !important;
} */
/* Disable the norton button */
/*
new FT update rendered this unnecessary
tr.navbarsearch > td:last-child {
    visibility: hidden !important;
}*/
/* Language bar */

.goog-te-gadget-simple {
    border: 0 !important;
}
/* Navbar Logo */

#navbar .logo img {
    opacity: 0 !important;
    -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=0)" !important;
}
#navbar .logo {
    width: 230px !important;
    height: 27px !important;
    background: url('https://osigalla-cdn.sirv.com/FTDark/ar1v6pa.png') !important;
    background-size: 230px 27px !important;
    background-repeat: no-repeat !important;
    background-position: bottom center !important;
}
/* Navbar Border */

#navbar {
    border-bottom: 0 !important;
}
/* Navbar Price Icons */

.navmenubutton_separator a b,
.navmenubutton_separator a[style="color:#EC7A23"] {
    font-weight: bold !important;
    color: #d9b977 !important;
}
/* Category Button on the navbar */

#navbar .focused {
    background-color: transparent !important;
}
#categoryButton {
    border: 0 !important;
    background: none !important;
}
/* Category Menu & Popouts in top nav and left nav */

.LinkedPopout {
    background-image: none !important;
    background-color: #666666 !important;
}
/* Top navigation menu popout */

.LinkedPopout li.Heading,
#CartPopout .ViewCart a,
#CartPopout .ViewCart a:visited {
    color: #d9b977 !important;
}
.LinkedPopout li a,
.LinkedPopout li a:visited {
    color: #e9e9e9 !important;
}
#CartPopout hr.hdivider {
    background: transparent !important;
    border-top: 1px dotted rgba(255, 255, 255, 0.3) !important;
}
/* Front page left side main menu  */

.PopoutPanel {
    background: #414141 !important;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5) !important;
}
#categoryPopout {
    top: 95px !important;
    padding-bottom: 0;
}
.PopoutPanel .topLevelCategories li {
    padding: 4px 5px 6px 10px !important;
    margin-left: -5px !important;
}
.PopoutPanel .topLevelCategories li.focused {
    background: none !important;
    background-color: #2e2e2e !important;
    border-radius: 5px !important;
}
.subCategories li {
    width: 150px !important;
}
.subCategories li:hover {
    background-color: #2e2e2e !important;
    box-shadow: none !important;
    border-radius: 5px !important;
    border-right: 0 !important;
}
.PopoutPanel .topLevelCategories li a:hover,
.subCategories li a:hover {
    color: #FFFFFF !important;
}
/* Frontpage sliders */

table.TightTable div[style="margin-left: 20px !important;"] {
    margin-left: 50px !important;
}

.frame {
    height: 380px;
    max-height: 380px;
    margin-top: 20px;
    margin-bottom: -20px;
}
/* #contents > .TightTable {
    margin-left: 250px !important;
} */



/* Frontpage Tabs */
.tabs-min .ui-widget-header {
    border-bottom: 1px dashed rgba(255, 255, 255, 0.2) !important;
    margin-bottom: 0em !important;
}

ul.ui-tabs-nav {
    text-align: center;
}

.ui-tabs .ui-tabs-nav li {
    display: inline-block;
    position: inherit;
    float: none;
    margin-left: 0 !important;
    text-transform: capitalize;
}
/* Product mini squares */

/* Get rid of the hardcoded background in the new style*/
#contents > div:nth-child(2)
{
    background: transparent !important;
}

/* Front page product box slider icons*/
.sly-prev {
    margin-left: 17px;
}
.sly-prev, .sly-next {
    border: 0;
    border-radius: 5px;
    height: 26px;
    background: linear-gradient(-45deg, #fbdf1c, #e6a42c);
    font-size: 20px;
    font-style: normal;
    font-family: "Arial,sans-serif";
    font-weight: bold;
    color: black;
    margin-top: 9px
}
.sly-prev.disabled, .sly-next.disabled {
    color: #8c8c8c;
    background: #626262 !important;
}

.scrollbar .handle {
    background: linear-gradient(-45deg, #fbdf1c, #e6a42c);
    border: 1px solid #454545;
    border-radius: 5px;
}

.scrollbar .track {
    height: 8px;
    margin-top: -28px;
    background: linear-gradient(0deg, rgba(0,0,0,0), #383838);
}

.section.ClearFloats {
    color: rgba(255,255,255,0.9);
    font-family: "Georgia";
    font-style: italic;
    font-weight: normal;
    font-size: 36px;
    text-align: center;
    text-transform: capitalize;
}

.CellMode .ProductGridItem {
    background-color: rgba(255, 255, 255, 0.1) !important;
    border: 0 !important;
    box-shadow: none !important;
    border-radius: 0 !important;
    padding-bottom: 15px !important;
    height: 325px !important;
    margin-right: 0px;
}

.GridItemCompare a, .GridItemCompare a:visited {
    color: white !important;
}
#HomepageTabs .ProductGridItem {
    height: 300px !important;
}
/* Mini squares in line mode */

.LineMode .ProductGridItem {
    display: -webkit-box !important;
    display: -ms-flexbox !important;
    display: flex !important;
    -webkit-box-align: center !important;
    -ms-flex-align: center !important;
    align-items: center !important;
    width: 480px !important;
    min-height: 180px !important;
    box-shadow: none !important;
    background: rgba(0, 0, 0, 0.2) !important;
    border-radius: 9px !important;
    padding: 5px 15px 5px 0 !important;
}
.LineMode .GridItemName {
    height: auto !important;
    max-height: inherit !important;
}
.LineMode .GridItemThumbnail {
    padding: 0px 15px !important;
    width: auto !important;
}
.LineMode .GridItemImage {
    width: auto !important;
    height: 90px !important;
}
.LineMode .GridItemTag {
    height: auto !important;
    color: rgba(255, 255, 255, 0.5) !important;
}
.LineMode .GridItemPricing {
    margin: 10px 0 10px 0 !important;
}
.LineMode .GridItemPrice {
    padding: 5px 0 !important;
    color: #FFFFFF !important;
    border-radius: 3px !important;
    /*background: -webkit-linear-gradient(315deg, #0679a5, #289f77) !important;
    background: linear-gradient(135deg, #0679a5, #289f77) !important;*/
    background: -webkit-linear-gradient(315deg, #6e878a, #7076a2) !important;
    background: linear-gradient(135deg, #6e878a, #7076a2) !important;
    text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.4) !important;
}
.LineMode .GridItemShipping {
    color: #747474 !important;
    font-size: 0.75em !important;
    width: 60px !important;
    padding-left: 10px !important;
}
/* Text just under the title and before rating */

.CellMode .GridItemThumbnail {
    margin-top: 20px !important;
}
.CellMode .GridItemName a,
.CellMode .GridItemName a:visited {
    color: rgba(255, 255, 255, 0.8) !important;
}
.CellMode .GridItemName a:hover {
    color: #5bc7f8 !important;
}
.CellMode .GridItemTag {
    color: rgba(255, 255, 255, 0.4) !important;
    overflow-wrap: break-word;
}
.CellMode .GridItemPrice {
    color: #FFFFFF !important;
    /*background: -webkit-linear-gradient(315deg, #0679a5, #289f77) !important;
    background: linear-gradient(135deg, #0679a5, #289f77) !important;*/
    /*background: -webkit-linear-gradient(315deg, #848484, #696969) !important;
    background: linear-gradient(135deg, #848484, #696969) !important;*/
    background: -webkit-linear-gradient(315deg, #6e878a, #7076a2) !important;
    background: linear-gradient(135deg, #6e878a, #7076a2) !important;
    text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.4) !important;
    width: 165px !important;
}
.CellMode .GridItemShipping {
    color: #747474 !important;
    font-size: 0.75em !important;
    width: 70px !important;
    text-align: center !important;
    height: 40px !important;
    padding-top: 5px !important;
}
/* Rating Stars */

.GridItemRating {
    margin: 4px 0 !important;
}
.Stars {
    background-image: url('https://osigalla-cdn.sirv.com/FTDark/yDRDeKr.png') !important;
}
.GrayStars {
    background-image: url('https://osigalla-cdn.sirv.com/FTDark/7nBugla.png') !important;
}
/* 
###############
# Forum View
###############
*/
/* Let's get rid of unnecessary borders */

.ForumsNavigation,
.ForumThread,
.ForumThread tbody tr td {
    border: 0 !important;
}
/* Forum table background glow */

.ForumThread,
.BGShadow {
    background: #1e1e1e !important;
    color: #CACACA !important;
    padding: 15px !important;
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.95) !important;
}
/* Selected forum on the left nav */

.ForumLink.Selected,
.LargeFilterEntry.FilterEntry.FilterSelected,
.FilterEntry.FilterSelected {
    background-color: #2e2d2d !important;
    margin-right: -6px !important;
    border-right: 6px solid #0989c4 !important;
    box-shadow: none !important;
}
/* Search box on top of the forum, also the input box to enter page number to jump to. Email sign up list in the footer */

.ForumSearchBox,
.JumpToBox,
.FooterLeft input,
.SingleLineTextBox,
.Quantities,
.VerticalTextBox,
.SingleLineTextArea,
.VerticalTextArea {
    background-color: #777777 !important;
    border: 0 !important;
    padding: 6px 4px !important;
    color: #FFFFFF !important;
}
.SingleLineTextArea {
    margin-bottom: 10px !important;
}
/* Page navigation. Boxed Numbers */

.Pager {
    color: #FFFFFF !important;
    margin-top: 12px !important;
    margin-bottom: 12px !important;
}
.Pager .ControlArrows {
    border: 0 !important;
    padding: 6px 0 !important;
    /* background-color: #0989c4 !important; */
    
    background: transparent !important;
}
.Pager .ControlArrows a,
.Pager .ControlArrows a:hover,
.ProductFilters .FilterCancellable a,
.ProductFilters .FilterCancellable a:visited {
    color: #FFFFFF !important;
}
.Pager .PageLink {
    padding: 6px 8px !important;
    border: 0 !important;
    /*background: linear-gradient(270deg, #444444, #333333) !important; */
    
    color: #CACACA !important;
    background: -webkit-radial-gradient(#2e2d2d, #1c1b1b) !important;
    background: radial-gradient(#2e2d2d, #1c1b1b) !important;
}
.Pager .PageLink:hover {
    color: #FFFFFF !important;
    background: -webkit-radial-gradient(#d51f44, #6e0016) !important;
    background: radial-gradient(#d51f44, #6e0016) !important;
}
.Pager .PageLink_Selected {
    border: 0 !important;
    padding: 6px 10px !important;
    color: #FFFFFF !important;
    background-color: #b50025 !important;
}
/* Forum Header and post seperator*/

.ForumHeader td,
.ForumThread tbody tr td.ForumHeader, .TicketResponses .TicketResponse {
    background: none !important;
    background-color: #1e1e1e !important;
}
/* Alternate forum thread color */

.ForumThread tbody tr {
    background-color: #1a1a1a !important;
}
.ForumThread tbody tr:nth-child(4n) {
    background-color: #1e1e1e !important;
}
/* Thread Title and Stats column */

.ThreadRowSubline,
.ThreadRowSubline a,
.ThreadStats {
    color: #5a5a5a !important;
}
/* Ratings label on a thread */

.ThreadRowSubline > .TinyLabel {
    margin: -3px 0 0 5px !important;
    border-radius: 5px !important;
    padding: 5px 7px !important;
    border: 0 !important;
    color: #FFFFFF !important;
    background-color: #313131 !important;
}
/* 
###############
# Thread view
###############
*/
/* Thread Header */

.ThreadCommandBar {
    /* background: linear-gradient(90deg, red,blue) !important; */
    
    background-color: #1A1A1A !important;
    padding: 10px 5px !important;
}
/* Product box in thread */

#content_ProductInfoPanel {
    background: -webkit-linear-gradient(right, #636363, #5b5b5b) !important;
    background: linear-gradient(270deg, #636363, #5b5b5b) !important;
    padding: 20px !important;
    border-radius: 9px !important;
    margin-top: 20px !important;
}
#content_ProductInfoPanel #content_FreeShipping,
#content_ProductInfoPanel,
#content_ProductName {
    color: rgba(255, 255, 255, 0.45) !important;
}
#content_ProductInfoPanel #content_ProductPrice {
    color: #e5e5e5 !important;
}
#content_ProductInfoPanel div:first-child div:first-child {
    margin-right: 25px !important;
    margin-top: 10px !important;
}
.PostContent a,
.PostContent a:visited {
    color: #d9b977 !important;
}
.PostContent a:hover {
    color: #ffffff !important;
}
/* User info in posts and in reviews */

.PostFlags ul {
    padding-left: 27px !important;
    padding-right: 7px !important;
}
.PostFlags ul li {
    list-style-type: none !important;
    text-align: center !important;
    border-bottom: 0 !important;
    background-color: #2a2828 !important;
    margin-bottom: 2px !important;
    padding: 6px 10px !important;
    color: #575757 !important;
}
/* For Staffs. Need JS to work. */

.StaffBadge {
    background: gold !important;
    padding: 5px 0 !important;
    width: 135px !important;
    margin-left: 8px !important;
    margin-top: 10px !important;
    box-shadow: 0px 0px 5px gold !important;
    color: black !important;
    font-weight: bold !important;
}
span.FlagValue {
    text-align: center !important;
    color: #999999 !important;
}
/* Rebate, kudos etc buttons */

a.FieldFlag,
a:visited.FieldFlag {
    color: black !important;
    text-shadow: none !important;
}
.FieldFlag[style="background:#EC7A23"] {
    background: #da4600 !important;
}
.FieldFlag {
    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5) !important;
    font-size: 8.5pt !important;
}
/* Edited message */

.TinyLabel {
    /* background-color: #562f8f !important; */
    
    color: #a38f26 !important;
    background-color: transparent !important;
    border-radius: 5px !important;
    border: 1px dashed #a38f26 !important;
    padding: 5px 10px !important;
    display: inline-block !important;
    margin: 0 3px 3px 0 !important;
}
.WarningBox {
    border: 0 !important;
    display: -webkit-box !important;
    display: -ms-flexbox !important;
    display: flex !important;
    -webkit-box-orient: vertical !important;
    -webkit-box-direction: normal !important;
    -ms-flex-direction: column !important;
    flex-direction: column !important;
    text-align: center !important;
    background: rgba(169, 13, 13, 0.66) !important;
    border-radius: 5px !important;
    color: #FFFFFF !important;
}
/* Editor */

.mbbc .mbbc-editor {
    color: #FFFFFF !important;
    background-color: #3a3a3a !important;
    margin-top: 10px !important;
    border-style: dotted !important;
}
.mbbc .mbbc-buttons li {
    border-radius: 0 !important;
}
/* Forum Signature */

div.PostContent + hr {
    visibility: hidden !important;
}
.ForumThread .Signature {
    border: 0 !important;
    border-top: 1px dotted #0989c4 !important;
}
/* Forum other tags */

a.SKUAutoLink {
    color: #FFFFFF !important;
    text-shadow: 0px 1px 3px rgba(0, 0, 0, 0.5) !important;
    border-radius: 5px !important;
    padding: 4px 10px !important;
    background: -webkit-linear-gradient(315deg, #006187, #088057) !important;
    background: linear-gradient(135deg, #006187, #088057) !important;
    border: 0 !important;
}
a.SKUAutoLink:hover {
    background: -webkit-linear-gradient(315deg, #289f77, #058cc0) !important;
    background: linear-gradient(135deg, #289f77, #058cc0) !important;
}
.ForumQuote {
    border: 0 !important;
    border-left: 5px #0D7BAD solid !important;
    background-color: #2a2828 !important;
    padding: 10px 0 !important;
}
/* Thread Popouts */

/* 
###############
# Footer
###############
*/
/* Remove one of the borders in the footer */

div#footer {
    /* background: linear-gradient(0deg, #282828,#444444) !important; */
    
    margin: 0 !important;
    padding-bottom: 30px !important;
}
.FooterBanner {
    border: none !important;
    padding: 30px 0 0 0 !important;
}
/* 
###############
#  Product Review
###############
*/
/* Hide the  under product info panel and fix spacing. */

#content_ProductInfoPanel hr {
    display: none !important;
}
#content_ProductInfoPanel .ClearFloats {
    margin: 0 !important;
}
/* Add some spacing after product info panel */

div.PageTitle + div#content_ProductInfoPanel {
    margin-bottom: 30px !important;
}
table.Reviews {
    margin-top: 15px !important;
}
/* Sidebar with user info on reviews */

.Reviews .PostFlags {
    background-color: #1e1e1e !important;
    border-right: 0 !important;
}
/* Pros and Cons */

td.Pros {
    background-color: #1E1E1E !important;
    border-right: 15px solid #1A1A1A !important;
}
td.Cons {
    background-color: #1E1E1E !important;
    border: 0 !important;
}
/* Sometimes pros and cons are shown side by side. */

.ProsConsTable td.Pros {
    border-right: 0 !important;
    border-bottom: 15px solid #1A1A1A !important;
}
/* Pros Cons table spacing */

.ReviewDate + .TightTable {
    margin: 60px 0 20px 0 !important;
}
.ReviewDate + .ProsConsTable {
    margin: 35px 0 20px 0 !important;
}
/* Remove the left border from review body */

.ReviewContent {
    border-left: 0 !important;
}
/* Submit your review product page button */

#Reviews-tab button:first-of-type {
    margin-top: 10px !important;
}
/* Photo Reviews */

.PhotoReviews,
.VideoReviews {
    box-shadow: none !important;
    border: 0 !important;
    background-color: transparent !important;
    text-align: center !important;
}
.PhotoReviews .Padded,
.VideoReviews .Padded {
    padding: 10px 0px !important;
    position: relative !important;
}
/* Add some spacing after "Read all reviews" and "Submit your review" */

.ClearFloats.EndOfInlineSection {
    margin-top: 30px !important;
}
/* When you click on a photo review */

.CenterFixedWidth .ClearFloats.EndOfInlineSection .Padded {
    background: #1e1e1e !important;
    color: #FFFFFF !important;
}
/* Make sure it doesn't affect the product review section */

#Reviews-tab .ClearFloats.EndOfInlineSection .Padded {
    background: none !important;
}
.CenterFixedWidth .ClearFloats.EndOfInlineSection .Padded hr {
    display: none !important;
}
/* Alternate row colors for reviews */
/* Remove the border between reviews */

table.Reviews > tbody > tr:nth-child(even) td {
    border: 0 !important;
    padding: 15px !important;
}
table.Reviews > tbody > tr:nth-child(odd) {
    background-color: #1A1A1A !important;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.85) !important;
}
/* 
###############
#  New Arrivals
###############
*/

.GridSortOptions {
    margin-right: 88px !important;
