style.css:
@-moz-document domain("leagueoflegends.wikia.com"), domain("leagueoflegends.fandom.com") {
/* Make nav bar dark */
.wds-global-navigation, .wds-global-navigation__account-menu, .wds-global-navigation__link, .wds-global-navigation__logo, .wds-global-navigation__notifications-menu, .wds-global-navigation__search, .wds-global-navigation__start-a-wiki, .wds-global-navigation__user-menu, .wds-global-navigation__wikis-menu {
    background-color: rgb(15,15,15)
}

.wds-global-navigation.wds-search-is-active .wds-global-navigation__search-input-wrapper, .wds-global-navigation.wds-search-is-always-visible .wds-global-navigation__search-input-wrapper {
    border-bottom: none
}

.wds-global-navigation__search-input, .wds-global-navigation__search-label {
    background-color: Black
}

/* Show all information tabs and hide tab nav*/
.hidden-tab {
    display: block !important;
}

.next-tab, .prev-tab {
    display: none;
}

/* Put the tabs in a box to make it clearer that they are related */
.skill_leveling {
    background: rgba(0,0,0,0.2);
    -moz-border-radius: 6px;
    border-radius: 6px;
}

/* Remove champion spotlight video */
div[itemprop="video"] {
    display: none;
}

/* Expand tabbed content (e.g. Corki W, Shaco Pets) */
.tabbertab {
    display: block !important;
}

.tabbernav {
    display: none;
}

/* Remove spacing between tabbed skills */
.tabbertab:not(:last-child)>p {
    display: none;
}

.tabbertab:last-child>:first-child {
    display: none;
}

.tabbertab>.skill {
    margin-top: 0 !important;
}

.tabbertab>div:last-of-type {
    display: none;
}
}
