style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("keystone-apps.stpeters.sa.edu.au") {
  /* Background */
  #ks-home, #ks-content {
    background: url("https://newevolutiondesigns.com/images/freebies/geometric-wallpaper-15.jpg") repeat fixed center center !important;
    background-size: initial !important;
  }

  /* Buttons */
  .ks-tile {
    background: #263238 !important;
    color: #FAFAFA !important;
    border-radius: 5px !important;
  }

  /* Credit in corner */
  .ks-wallpaper-attribution:after {
    content: "Original style by Louis Rust";
    padding: 3px 10px;
    border-radius: 5px;
    font-size: .9em;
    background-color: rgba(0,0,0,0.5);
    color: #eee;
  }
  .ks-wallpaper-attribution span {
    display: none;
  }
  
  /* Shadows */
  * {
    text-shadow: 0 0px !important;
    box-shadow: 0 0px !important;
  }
}
