style.css:
body {
	margin: 0 auto;
	padding: 3rem;
	font-size: 19px;
	line-height: 1.33;
	color: #444;
	max-width: 50rem;
	box-sizing: border-box;
	background-color: #fafafa;
}

strong, h1, h2, h3 {
	color: #000;
}

code {
	font-size: .9rem;
	color: #666;
}

footer {
	padding: 2rem 0 0;
}
