style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("avenoel.org") {
body  {
    background:#eca1a6;
   
  }
.navbar-inverse {
    background-color: pink;
}
.footer {
    background-color: pink;  
}
a {color:#F9F9F9;}
td {color:#F9F9F9;}
.topics-author a {
    color: #F9F9F9;
}
.mod {
    color: #F9F9F9 !important;
}
.topic-icon{background-image:none;}

.topic-icon img {
    width: 0px;
}


.navbar-logo img{height:0px; width:0px; }
.navbar-logo {
	padding-top:50px;
	/* these numbers match the new image's dimensions */
	padding-left: 200px !important;
	padding-top: 41px !important;
	background: url("https://i.imgur.com/CDZ4Wuv.png") no-repeat !important;
}



.navbar-inverse .navbar-nav>.active>a, .navbar-inverse .navbar-nav>.active>a:hover, .navbar-inverse .navbar-nav>.active>a:focus {
    color: #fff;
    background-color:#eca1a6;
}

.navbar li.active a {
    background: #eca1a6 !important;
}
a.active {color:red;}
}
