style.css:
@-moz-document domain("www.resetera.com") {
body, #content .pageContent, .titleBar h1, .messageText, .bbCodeQuote .attribution, .sectionFooter, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .pairsRows, .pairsColumns, .pairsJustified
{   
    color: /*[[textfontcolor]]*/ !important;
}

html, #header, .discussionListItem, .discussionListItem .posterAvatar, .discussionListItem .stats, #content .pageContent, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .navTabs, .messageList .message, .messageUserInfo, .node .nodeLastPost, .sectionFooter, .PageNav a.currentPage, body .itemPageNav a, .breadcrumb, .threadListSeparator, .discussionList .sectionHeaders, .secondaryContent, .nodeList .categoryStrip, .sidebar .section .primaryContent   h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .footer .pageContent, #DiscussionListOptionsHandle a
{
	background-color: /*[[backgroundcolor]]*/ !important;
}

.bbCodeQuote, .bbCodeQuote .attribution, .bbCodeQuote .quoteContainer, .PageNav a
{
	background-color: /*[[quotebackgroundcolor]]*/ !important;
}

a[href]:hover, a:link, a:visited {   
    color: /*[[linkfontcolor]]*/ !important;
}

.discussionListItem {
    border-right-color: /*[[bordercolor]]*/ !important;
    border-bottom-color: /*[[bordercolor]]*/ !important;
    border-left-color: /*[[bordercolor]]*/ !important;
}

.PageNav a
{
border: 1px solid /*[[pagebordercolor]]*/ !important;
}

}
