@-moz-document domain(avenoel.org)
{
/*____ | Thème créé par Talsadoum, Ruth, Tigriz et Ugly pour avenoel.org |  ____*/

/* Fond d'écran */

body {
    background-image: url(data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==) !important;
    background-repeat: repeat !important;
    background-color: #181818 !important;
}

.bottombar ul {
    background: #1f1f1f;
}

.topic-icon img {
    width: 14px;
    background-color: transparent;
}

a:hover, a:focus {
    color: #636b6f;
    text-decoration: none;
}



h1, .h1, h2, .h2, h3, .h3 {
    margin-top: 22px;
    margin-bottom: 11px;
    font-family: "robotoboldcondensed",Arial,Helvetica,sans-serif;
    font-weight: bolder;
    line-height: 1.1;
}

hr {
display:none;
}

body > main > div > div.col-md-3.col-sm-12.col-xs-12.pull-right.hidden-sm.hidden-xs > section.aside > img {
    display:none;
}

body > main > div > div.col-xs-12.col-sm-12.col-md-9 > section > table{
    display: table-row;
    font-size: 0.6875rem;
    vertical-align: middle;
    line-height: 1rem;
    list-style-type: none;
    padding: 0.125rem 0;
    font-family: Tahoma,"DejaVu Sans Condensed",Arial,Helvetica,sans-serif;
    border: 0.0625rem solid #ccc;
    width: 100%;
    table-layout: fixed;
    border-collapse: separate;
    border-spacing: 0 0.125rem;
}

.navbar .navbar-logo img{
    margin: -2px 0 0 0;
	height: 0 !important;
	width: 0 !important;
	/* these numbers match the new image's dimensions */
	padding-left: 113px !important;
	padding-top: 22px !important;
	background: url(http://image.noelshack.com/fichiers/2017/45/5/1510352710-avenoelsmall.png) no-repeat !important;
}

/* Liens */




/* Bouton topic*/
.btn-primary {
    color: #fff;
    background-color: #333333;
    border-color: #111111;
}

.btn-primary:hover, .btn-primary:active, .btn-primary:focus {
    color: #fff;
    background-color: #B71C1C;
    border-color: #111111;
}


.pagination.pagination-topic a.active {
    background: #B71C1C;
    color: #FFF;
}

.form-control:focus {
    border-color: #B71C1C;
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.075), 0 0 8px rgba(232, 152, 152, 0.6);
}

.topics-author a {
    color: #acacac;
}
a {
    color: #acacac;
    text-decoration: none;
}
.breadcrumb {
    background-color: rgba(107, 107, 107, 0)!important;
    font-family: "robotoboldcondensed",Arial,Helvetica,sans-serif;
    font-weight: bolder;
}
main.container .container-content section {
    background-color: #1f1f1f!important;
}
.topic-message .message-header {
position: relative;
padding-bottom: 45px;
border-bottom: 1px dotted #acacac;
}
.topic-message .message-header .message-username a {
    color: #acacac;
}
.topic-message .message-header .message-actions {
    margin: 0;
    display: inline-block;
    padding: 0;
    background: none;
}
.topic-message .message-header .message-date {
    font-size: 12px;
    background: none;
}
.pagination.pagination-topic a {
    color: #acacac;
}
.topic-message {
    border: 0px solid #545454;
    background: rgba(247, 202, 24, 0.08);
    color: #acacac;
    margin-bottom: 10px;
    padding: 0;
    overflow: hidden;
    display: -webkit-box;
    display: flex;
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.075), 0 0 15px rgba(0, 0, 0, 0.6);
}

.topic-message .message-content {
    padding: 5px 0;
    font-size: 14px;
    word-wrap: break-word;
    position: relative;
    min-height: 0px;
    overflow: hidden;
}

.topics-author {
    background: #1f1f1f;
    text-align: left;
    width: 120px;
}
.topics {
    font-size: 0.9em;
    margin-bottom: 1px;
    border: 1px solid #646667;
}
.breadcrumb {
    padding: 1px 1px;
    margin-bottom: 1px;
    list-style: none;
    background-color: #1f1f1f;
    border-radius: 1px;
}
.breadcrumb {
    padding: 0 0 1px 0;
    margin: 0 0 1px 0;
    border-bottom: 1px solid #1f1f1f;
    font-size: 12px;
}
.preview {
    border: 1px dashed #646667;
    color: #acacac;
    background-color: #1f1f1f;
    padding: 1em;
    display: none;
}
main.container .container-content section {
    background: #1f1f1f;
    padding: 0.5rem 1.25rem;
    border-radius: 5px;
}
body {
font-family: "Roboto", sans-serif;
font-size: 14px;
line-height: 1.2;
color: #acacac;
background-color: #f5f8fa;
}
.table > thead > tr > th,
.table > thead > tr > td,
.table > tbody > tr > th,
.table > tbody > tr > td,
.table > tfoot > tr > th,
.table > tfoot > tr > td {
    background: #1f1f1f;
    padding: 1px;
    line-height: 1.6;
    vertical-align: top;
    border-top: 1px solid #ddd;
}
tbody td {
    background: #1f1f1f;
    padding: 4px;
}
td {
    background: #1f1f1f;
    border-style: none !important;
}
td,
th {
    background: #1f1f1f;
    padding: 0;
}
* {
    background: #1f1f1f;
    box-sizing: border-box;
}
user agent stylesheet td,
th {
    background: #1f1f1f;
    display: table-cell;
    vertical-align: inherit;
}

body {
    font-family: "Roboto", sans-serif;
    font-size: 13px;
    line-height: 1.4;
    color: #acacac;
    background-color: #f5f8fa;
}
.form-control {
    display: block;
    width: 100%;
    height: 36px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.6;
    color: #acacac;
    background-color: #191919;
    background-image: none;
    border: 1px solid #f5f8fa;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    transition: border-color ease-in-out 0.15s, box-shadow ease-in-out 0.15s;
}
.breadcrumb:before {
    content: "respawn";
    color: #888;
}
/* citations */

.message-content-quote{
margin-left:20px;
display: block;
padding: 5px;
padding-bottom: 0px;
padding-top: 0px;
background: #282828 !important;
border-left: 5px solid #717171;
color: #848484;
line-height: 0px;
letter-spacing: normal;
}


.message-content-quote a{
padding: 10px;
display: inline-block;
font-size: 14px;
background:#282828;
color: #848484;
font-weight: 600;
letter-spacing: normal;
text-decoration: none;
}
.message-content-quote img.board-noelshack {
background:#282828;
}

.message-content-quote + br{
display: none;
}
.message-content-quote * {
font-size: 14px;
color: #848484;
background:#282828;
letter-spacing: normal;
margin-left: 0px;
border-left: 0px dotted #f7d9d7;
padding-left: 5px;
line-height: 20px;
}


/* Ce qui concerne création de topic ou poster un message */

.spoiler .spoiler-btn {
    color: #acacac;
    cursor: pointer;
}
.spoiler .spoiler-content {
    border: 1px dashed #1f1f1f;
    color: #acacac;
    background-color: #1f1f1f;
    padding: 1em;
}
.bbcodes {
    text-align: left;
    margin: 0;
    border: 1px solid #646667;
    background: #1f1f1f;
}
hr {
    margin-top: 22px;
    margin-bottom: 22px;
    border: 0;
    border-top: 1px solid #1f1f1f;
}
.forum-tools .btn {
    background: #1f1f1f;
    border: 1px solid #646667;
    color: #acacac;
    text-transform: uppercase;
    font-size: 0.9em;
}
.btn:hover,
.btn:focus,
.btn.focus {
    color: #F5F5F5;
    text-decoration: none;
}
.form-control {
    display: block;
    width: 100%;
    height: 36px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.6;
    color: #acacac;
    background-color: #1f1f1f;
    background-image: none;
    border: 1px solid #646667;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
    transition: border-color ease-in-out 0.15s, box-shadow ease-in-out 0.15s;
}
.pagination > li > a,
.pagination > li > span {
    position: relative;
    float: left;
    padding: 6px 12px;
    line-height: 1.6;
    text-decoration: none;
    color: #acacac;
    background-color: #1f1f1f;
    border: 1px solid #646667;
    margin-left: -1px;
}
.table > thead > tr > th {
    vertical-align: bottom;
    border-bottom: 2px solid #646667;
}
/* Concerne le chat */

.chat-box .chat-message .chat-message-username a {
    color: #acacac;
}
.chat-box .chat-message {
    color: #676767;
    overflow: hidden;
}
.chat-form {
    background: #1f1f1f;
    padding: 20px;
}
.chat-box {
    height: 500px;
    overflow: auto;
    padding: 0;
    list-style: none;
    font-size: 14px;
    word-wrap: break-word;
}

/* Profile */

.profile .profile-title,
.profile .profile-wrapper,
.profile .profile-footer,
.profile .profile-biography {
    background: #1f1f1f;
    padding: 15px;
    border-radius: 5px;
}
.profile .profile-title {
    color: #acacac;
    text-align: center;
    margin: 0 0 15px 0;
}

/* Barre de navigation */

.navbar-inverse .navbar-nav > li > a {
    color: #acacac;
}

.navbar-inverse .navbar-nav > .active > a,
.navbar-inverse .navbar-nav > .active > a:hover,
.navbar-inverse .navbar-nav > .active > a:focus {
    color: #acacac;
    background-color: #080808;
}
.navbar-inverse {
    background-color: #1f1f1f;
}
.navbar li.active a {
    background: #1f1f1f !important;
}
body div#navbar {
    background-color: #202020!important;
}
.pagination > li > a:hover,
.pagination > li > a:focus,
.pagination > li > span:hover,
.pagination > li > span:focus {
    z-index: 2;
    color: #cacaca;
    background-color: #1f1f1f;
    border-color: #646667;
}
/* smiley */

.fancybox-skin {
    position: relative;
    background: #1f1f1f;
    color: #acacac;
    text-shadow: none;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}
/* cloche chiante */

.onesignal-bell-svg {
